    return "<?xml version=\"1.0\" encoding=\"iso-8859-1\" ?> <courseware><lesson_data><acesVer value=\"2.2.6632.18362\"/><published date=\"6/19/2018\" time=\"3:24 PM\" publishedby=\"1045002833E\"/><lessonID value=\"9\"/><lessonIdentifier value=\"T38_SO0309_CBT\"/><lessonRevision value=\"180501\"/><title value=\"Heads-Up Display (HUD)\"/><program value=\"T38\"/><course value=\"SO\"/><lesson value=\"0309\"/><mediatype value=\"CBT\"/><splashScreen value=\"T38_Splash_Systems.png\"/><backgroundColor value=\"0x000066\"/><highlightColor value=\"0x00FF00\" value2=\"0xFF9900\" value3=\"0xFF0000\" value4=\"0xFFFFFF\" value5=\"0x00FFFF\" value6=\"0xFFFF00\"  /><drawObjectsLineWidth value=\"4\"/><hotSpotColor value=\"0xFFFF00\"/><classified value=\"0\"/><classlevel value=\"0\"/><fullNavigation value=\"1\"/><bases><base name=\"Columbus\" code=\"KCBM\" /><base name=\"Randolph\" code=\"KRND\" /><base name=\"Sheppard\" code=\"KSPS\" /></bases><stinfo show=\"True\"><![CDATA[DISTRIBUTION D — Distribution authorized to Department of Defense and U.S. DoD contractors only for administrative or operational use, 1 May 18. Refer other requests for this document to 19 AF/OSSU, Randolph AFB, TX 78150-4325. <br /><br />WARNING — This document contains technical data whose export is restricted by the Arms Export Control Act (Title 22, U.S.C. 2751 et seq.) or the Export Administration Act of 1979, as amended, Title 50, U.S.C., App. 2401, et seq. Violation of these export-control laws is subject to severe criminal penalties. Dissemination of this document is controlled under DoD Directive 5230.25.<br /><br />DESTRUCTION NOTICE — Destroy by any method that will prevent disclosure of contents or reconstruction of the document.]]></stinfo></lesson_data><course_data><segment id=\"14\" num=\"1\" title=\"HUD Symbology\"  type=\"0\" ><topic id=\"346\" num=\"1\" title=\"Lesson Objectives\"  type=\"0\"><frame id=\"t346\" num=\"1\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" title=\"Heads-Up Display (HUD)\" attachment=\"\" headphoneIcon=\"None\" menutitle=\"Topic Intro\" template=\"topicIntro\" numbuilds=\"1\" remediation=\"\" classlevel=\"0\" txtWidth=\"1000\" txtHeight=\"800\" txtLeft=\"150\" txtTop=\"0\" columnA=\"\" columnB=\"\" ><text bldnum=\"1\"><![CDATA[<span class='tn'><span class='th'>&emsp;HUD Symbology</span><br><span class='th'>&emsp;&emsp;- Lesson Objectives</span><br>&emsp;&emsp;- Introduction<br>&emsp;&emsp;- Display and Controls<br>&emsp;&emsp;- NAV Mode Display<br>&emsp;&emsp;- TCAS Display<br>]]></text></frame><frame id=\"4613\" num=\"2\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Objective\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Objective\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"990\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"301\" colTop=\"0\" colWidth=\"375\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'><span class='an'>1.&nbsp;&nbsp;&nbsp;&nbsp;Know the controls and displays for the F-16 emulation on the Head-Up Display (HUD).</span></span></P><P><span class='an'><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Samples of Behavior:</span></span></P><P><span class='an'><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;a.&nbsp;&nbsp;&nbsp;&nbsp;Identify the symbology on the T-38C F-16 emulation HUD display in the NAV master mode.</span></span></P><span class='an'><span class='an'><P><span class='an'><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;b.&nbsp;&nbsp;&nbsp;&nbsp;Explain the aircraft flight parameters and data displayed on the T-38C F-16 emulation HUD.</span></span></P><P><span class='an'><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;c.&nbsp;&nbsp;&nbsp;&nbsp;Describe the <span class='an'>Traffic Collision and Avoidance System (</span>TCAS) resolution advisory (RA) command<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; symbology displayed on the&nbsp;HUD</span></span>.</P><P>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;d.&nbsp;&nbsp;&nbsp;&nbsp;Explain the selection procedures for removing data displayed on the T-38C HUD in the NAV master<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;mode using the declutter (DCL) function.</span></span></P><P><span class='an'><span class='an'>&nbsp;</P></span></span>]]></text></frame></topic><topic id=\"37\" num=\"2\" title=\"Introduction\"  type=\"0\"><frame id=\"t37\" num=\"1\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" title=\"Heads-Up Display (HUD)\" attachment=\"\" headphoneIcon=\"None\" menutitle=\"Topic Intro\" template=\"topicIntro\" numbuilds=\"1\" remediation=\"\" classlevel=\"0\" txtWidth=\"1000\" txtHeight=\"800\" txtLeft=\"150\" txtTop=\"0\" columnA=\"\" columnB=\"\" ><text bldnum=\"1\"><![CDATA[<span class='tn'><span class='th'>&emsp;HUD Symbology</span><br>&emsp;&emsp;- Lesson Objectives<br><span class='th'>&emsp;&emsp;- Introduction</span><br>&emsp;&emsp;- Display and Controls<br>&emsp;&emsp;- NAV Mode Display<br>&emsp;&emsp;- TCAS Display<br>]]></text></frame><frame id=\"516\" num=\"2\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Lesson Introduction\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Lesson Introduction\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"841\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"300\" colTop=\"50\" colWidth=\"375\" ><text bldnum=\"1\" ><![CDATA[&nbsp;<BR>]]></text><column id=\"99\" bldNum=\"1\"><![CDATA[<span class='c'><span class='aeL'><span class='ae'><B>HEAD-UP DISPLAY (HUD)</B><BR><BR></span></span><span class='an'><span class='l'><span class='c'><span class='an'><span class='an'><span class='l'><span class='an'><span class='an'>The T-38C is equipped with a single HUD that is designed as a primary flight reference for all operations. </span></span><span class='an'><span class='an'><span class='an'>The HUD is used in conjunction with the UFCP to display all required flight data. <BR><BR>This lesson will </span><span class='l'><span class='c'><span class='l'><span class='c'><span class='l'><span class='l'>familiarize you with the basic features of the F-16 emulation HUD. </span></span></span></span></span></span></span></span><span class='an'></span></span><BR></span></span></span></span></span></span></span>]]></column><files mediaID=\"8018\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD_blank_P00200.jpg\" media_x=\"95\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /></frame><frame id=\"731\" num=\"3\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Overview\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Overview\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"990\" txtHeight=\"254\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The HUD lesson describes the basic design features and display characteristics of the T-38C F-16 emulation HUD. The following HUD topics are covered in the HUD Symbology segment:</span></P><P><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Display and Controls&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;TCAS Display</span></P><P><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;NAV Mode Display</span> <BR></P>]]></text><files mediaID=\"11725\" bldnum=\"1\" media=\"T38_SO1109_CBT-HUDsplashVOR13pt8-45sec_b9.png\" media_x=\"35\" media_y=\"160\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /></frame></topic><topic id=\"45\" num=\"3\" title=\"Display and Controls\"  type=\"0\"><frame id=\"t45\" num=\"1\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" title=\"Heads-Up Display (HUD)\" attachment=\"\" headphoneIcon=\"None\" menutitle=\"Topic Intro\" template=\"topicIntro\" numbuilds=\"1\" remediation=\"\" classlevel=\"0\" txtWidth=\"1000\" txtHeight=\"800\" txtLeft=\"150\" txtTop=\"0\" columnA=\"\" columnB=\"\" ><text bldnum=\"1\"><![CDATA[<span class='tn'><span class='th'>&emsp;HUD Symbology</span><br>&emsp;&emsp;- Lesson Objectives<br>&emsp;&emsp;- Introduction<br><span class='th'>&emsp;&emsp;- Display and Controls</span><br>&emsp;&emsp;- NAV Mode Display<br>&emsp;&emsp;- TCAS Display<br>]]></text></frame><frame id=\"725\" num=\"2\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"1\" title=\"HUD Display\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"HUD Display\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"447\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'><span class='an'>The Head-Up Display (HUD) is only in the front cockpit and is designed as a primary flight reference (PFR) for all-weather operations. All of the required flight attitude and navigational information is projected onto the HUD, allowing the pilot to fly the aircraft while maintaining positional awareness.</span></span></P><P><span class='an'><span class='an'>The avionics system can display two different HUD formats:</span></span></P><P><span class='an'><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;F-16 emulation</span></span></P><P><span class='an'><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;MIL-STD-1787C</span></span></P><P><span class='an'><span class='an'>Each display format has several modes and sub-modes, but this lesson only covers the F-16 emulation HUD in the NAV master mode.</span></span></P><P><span class='an'><span class='an'></span></span><span class='an'><I>The other weapons related modes are covered in&nbsp;IFF </I><I>training lessons.</I></span> <BR><span class='ae'><I><B></B></I></span>&nbsp;<BR></P>]]></text><files mediaID=\"11726\" bldnum=\"1\" media=\"T38_SO1109_CBT_BlankHUD-camera.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /></frame><frame id=\"733\" num=\"3\" revision=\"1.0\" tempType=\"1\" quesType=\"tf\" randomize=\"0\" title=\"Display Unit\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Display Unit\" template=\"NotUsed\" numbuilds=\"2\" remediation=\"0\" classlevel=\"0\" txtWidth=\"451\" txtHeight=\"635\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The display unit is a collimated display of flight information is projected through a lens onto the HUD<span class='an'><span class='an'>&nbsp;combining glass</span></span></span><span class='an'>, or optical sight.</span></P>]]></text><text bldnum=\"2\" ><![CDATA[<P><span class='an'>A </span><span class='ae'>HUD camera </span><span class='an'>is located at the base of the combining glass above the UFCP. The camera is shown here from a viewing position that is lower than you will normally sit in the cockpit. You will actually see the projected display without obstruction from the camera from the normal sitting height.</span></P><P><span class='an'><i>For the purposes of this lesson, the camera is removed from the HUD display examples.</i></span> <BR></P>]]></text><files mediaID=\"11727\" bldnum=\"1\" media=\"T38_SO1109_CBT_BlankHUD-camera.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11728\" bldnum=\"2\" media=\"T38_SO1109_CBT_BlankHUD-camera.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"1764\" bldnum=\"1\" type=\"rectangle\" x1=\"543\" y1=\"80\" x2=\"409\" y2=\"488\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"1765\" bldnum=\"2\" type=\"rectangle\" x1=\"690\" y1=\"492\" x2=\"115\" y2=\"135\" htext=\"\" colorType=\"1\" /></frame><frame id=\"747\" num=\"4\" revision=\"1.0\" tempType=\"1\" quesType=\"tf\" randomize=\"0\" title=\"HUD Field of View\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Field of View\" template=\"NotUsed\" numbuilds=\"2\" remediation=\"0\" classlevel=\"0\" txtWidth=\"466\" txtHeight=\"634\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The projected </span><span class='ae'>field of view <span class='an'>(FOV)</span> </span><span class='an'>on the display was designed to be seen from a particular viewing distance and angle which should closely approximate the sitting position you use in the </span><BR><span class='an'>T-38C. If your actual position is significantly<BR>different the design position, you will not see all of the symbology displayed within the HUD's </span><span class='ae'>instantaneous field of view </span><span class='an'>(</span><span class='an'>IFOV</span><span class='an'>) without moving your head around.</span> </P>]]></text><text bldnum=\"2\" ><![CDATA[<P><span class='an'>The HUD uses a 24</span><span class='an'>°</span> <span class='an'>total field of view (<span class='ae'>TFOV</span>). The IFOV is the actual area within the TFOV that you can see on the HUD glass from the viewing position. The HUD's <span class='an'><I>Design Eye Position </I></span>has a 15</span><span class='an'>°</span> <span class='an'>IFOV </span><span class='an'>and the slightly closer <span class='ae'><span class='an'><I>Combat Eye Position </I></span><span class='an'>has </span></span>a 17</span><span class='an'>°</span> <span class='an'>IFOV</span><span class='an'>. Most of the symbology is displayed within the IFOV. Depending on sitting position, you may need some head movement to clearly see data on the edge or outside of the IFOV.</P></span>]]></text><files mediaID=\"11828\" bldnum=\"1\" media=\"T38_SO1109_CBT_PilotHUDsightLine.png\" media_x=\"515\" media_y=\"55\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11853\" bldnum=\"2\" media=\"T38_SO1109_CBT_PilotHUDsightLine.png\" media_x=\"515\" media_y=\"55\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /></frame><frame id=\"762\" num=\"5\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"1\" title=\"HUD Control Switches\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Control Switches\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"494\" txtHeight=\"638\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>Power to the HUD and brightness level of the HUD symbology are controlled using the following<span class='an'>&nbsp;HUD control switches </span></span><span class='an'>on the front cockpit UFCP:</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ON/OFF Switch</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;NT/AUT/DAY Switch</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;H BRT Switch</span> <BR></P>]]></text><files mediaID=\"11222\" bldnum=\"1\" media=\"T38_SO1109_CBT_UFCPBlank.png\" media_x=\"525\" media_y=\"60\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"5862\" bldnum=\"1\" type=\"rectangle\" x1=\"929\" y1=\"329\" x2=\"50\" y2=\"74\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"5863\" bldnum=\"1\" type=\"rectangle\" x1=\"545\" y1=\"352\" x2=\"110\" y2=\"50\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"5864\" bldnum=\"1\" type=\"rectangle\" x1=\"673\" y1=\"356\" x2=\"72\" y2=\"45\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1096\" num=\"6\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"1\" title=\"HUD ON / OFF Switch\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"On / Off Switch\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"489\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'><span class='an'>The front cockpit UFCP</span> </span><span class='an'>ON/OFF switch </span><span class='an'>controls the power to the HUD, as well as to both UFCPs and the HUD CTVS camera.&nbsp;</span></P><P><span class='an'>The&nbsp;rear cockpit UFCP ON/OFF switch is inoperative.</span> <BR></P>]]></text><files mediaID=\"11223\" bldnum=\"1\" media=\"T38_SO1109_CBT_UFCPVOR3U4V.png\" media_x=\"525\" media_y=\"60\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"1778\" bldnum=\"1\" type=\"rectangle\" x1=\"928\" y1=\"330\" x2=\"50\" y2=\"74\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1128\" num=\"7\" revision=\"1.0\" tempType=\"1\" quesType=\"mcT\" randomize=\"1\" title=\"NT / AUT / DAY Switch\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"NT/AUT/DAY\" template=\"NotUsed\" numbuilds=\"4\" remediation=\"0\" classlevel=\"0\" txtWidth=\"488\" txtHeight=\"634\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>This 3-position switch (</span><span class='an'>NT/AUT/DAY</span><span class='an'>) is used to select the desired brightness mode of the HUD and UFCP.</span> </P>]]></text><text bldnum=\"2\" ><![CDATA[<P><span class='ae'><B>NT (Night)</B></span></P><P><span class='ae'></span><span class='an'>The &quot;NT&quot; switch position selects the night brightness range for the HUD and UFCP. The actual brightness level of the HUD is controlled using the &quot;H BRT&quot; rocker key.</span> </P>]]></text><text bldnum=\"3\" ><![CDATA[<P><span class='ae'><B>AUT (Automatic)</B></span></P><P><span class='ae'></span><span class='an'>The &quot;AUT&quot; switch position selects the automatic HUD brightness mode. The AUT mode maintains a constant ratio between the brightness of the display and surrounding environment. The initial ratio is determined by the &quot;H BRT&quot; key setting. The brightness level is adjusted as the outside surrounding light increases or decreases.</span> </P>]]></text><text bldnum=\"4\" ><![CDATA[<P><span class='ae'><B>DAY</B></span> </P><P><span class='an'>The &quot;DAY&quot; switch position selects the day brightness range for the HUD and UFCP. The actual brightness level of the HUD is controlled using the &quot;H BRT&quot; rocker key.</span> <BR></P>]]></text><files mediaID=\"11224\" bldnum=\"1\" media=\"T38_SO1109_CBT_UFCPVOR3U4V.png\" media_x=\"525\" media_y=\"60\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11225\" bldnum=\"2\" media=\"T38_SO1109_CBT_UFCPVOR3U4V.png\" media_x=\"525\" media_y=\"60\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11226\" bldnum=\"3\" media=\"T38_SO1109_CBT_UFCPVOR3U4V.png\" media_x=\"525\" media_y=\"60\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11227\" bldnum=\"4\" media=\"T38_SO1109_CBT_UFCPVOR3U4V.png\" media_x=\"525\" media_y=\"60\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"1791\" bldnum=\"1\" type=\"rectangle\" x1=\"545\" y1=\"352\" x2=\"110\" y2=\"50\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"1793\" bldnum=\"2\" type=\"rectangle\" x1=\"559\" y1=\"365\" x2=\"35\" y2=\"40\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"1796\" bldnum=\"4\" type=\"rectangle\" x1=\"609\" y1=\"365\" x2=\"35\" y2=\"40\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"1797\" bldnum=\"3\" type=\"rectangle\" x1=\"578\" y1=\"353\" x2=\"42\" y2=\"50\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1136\" num=\"8\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"1\" title=\"HUD Brightness Control (H BRT)\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"H BRT\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"483\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<span class='an'>The </span><span class='ae'><span class='an'>HUD Brightness (H BRT) </span></span><span class='an'>switch controls the brightness level of the HUD display. Pressing the right [ + ] side of the rocker switch increases brightness, while pressing the left [ </span><span class=an'>–</span> <span class='an'>] side decreases the brightness.</span> <BR><span class='an'></span>&nbsp;<BR>]]></text><files mediaID=\"11228\" bldnum=\"1\" media=\"T38_SO1109_CBT_UFCPVOR3U4V.png\" media_x=\"525\" media_y=\"60\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"1798\" bldnum=\"1\" type=\"rectangle\" x1=\"673\" y1=\"356\" x2=\"72\" y2=\"45\" htext=\"\" colorType=\"1\" /></frame><frame id=\"783\" num=\"9\" revision=\"1.0\" tempType=\"1\" quesType=\"mcT\" randomize=\"1\" title=\"HUD Function Key\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"HUD Function Key\" template=\"NotUsed\" numbuilds=\"2\" remediation=\"0\" classlevel=\"0\" txtWidth=\"476\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<span class='an'>The<span class='an'>&nbsp;HUD function key </span></span><span class='an'>on the UFCP selects the HUD page </span><span class='an'>which provides various display options.</span> <BR>]]></text><text bldnum=\"2\" ><![CDATA[&nbsp;<br>]]></text><files mediaID=\"11229\" bldnum=\"1\" media=\"T38_SO1109_CBT_UFCPVOR3U4V.png\" media_x=\"525\" media_y=\"60\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"8035\" bldnum=\"1\" media=\"T38_SO1109_CBT_Comment_box_P00332.jpg\" media_x=\"52\" media_y=\"330\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11635\" bldnum=\"2\" media=\"T38_SO1109_CBT_UFCP-HUDpage1_982_669.swf\" media_x=\"34\" media_y=\"53\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"1599\" bldnum=\"1\" type=\"text\" x1=\"102\" y1=\"378\" x2=\"317\" y2=\"43\" htext=\"Click on the HUD function key to view the HUD page and continue.\" colorType=\"4\" /><hotspot hotSpotID=\"128\" bldNum=\"1\" actionType=\"Move to Next\" activateOn=\"Click\" action=\"0\" displayType=\"Outlined\" left=\"740\" top=\"306\" width=\"42\" height=\"42\" mediaX=\"0\" mediaY=\"0\" textWidth=\"100\" textHeight=\"100\" dropShadow=\"0\" /></frame><frame id=\"792\" num=\"10\" revision=\"1.0\" tempType=\"1\" quesType=\"mcT\" randomize=\"0\" title=\"HUD Declutter (DCL) Key\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Declutter Function\" template=\"NotUsed\" numbuilds=\"3\" remediation=\"0\" classlevel=\"0\" txtWidth=\"449\" txtHeight=\"638\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The final key on the UFCP that affects the HUD display is the </span><span class='ae'><span class='an'><span class='ae'>Declutter (DCL) key</span></span></span><span class='an'>. The DCL key toggles the HUD declutter function ON and OFF. The initial default setting is OFF.</span></P><P><span class='an'></span><span class='an'>When declutter is </span><span class='an'>ON</span><span class='an'>, changing the primary navigation source will not affect the declutter state. However, the HUD will revert back to a full (</span><span class='an'>DCL OFF</span><span class='an'>) display whenever a different master mode is selected. In this case, you must press the </span><span class='ae'><span class='an'>DCL</span> </span><span class='an'>key again to declutter the new master mode display.</span></P><P><span class='an'></span><span class='an'>The specific items removed from the HUD display are selected prior to flight on the </span><span class='ae'>JMPS declutter screen</span><span class='an'>.</span></P>]]></text><text bldnum=\"2\" ><![CDATA[<P><span class='an'>Items that are unchecked (not selected) on the declutter screen&nbsp;are </span><span class='an'><I><B>not&nbsp;</B></I></span><span class='an'>displayed. In this example, the system will remove&nbsp;</span><span class='ae'>Current G</span><span class='an'>, </span><span class='ae'>Max G</span><span class='an'>, </span><span class='ae'>Safe/Arm Display</span><span class='an'>, </span><span class='ae'>TD Box</span><span class='an'>, and </span><span class='ae'>Bullseye </span><span class='an'>from the HUD NAV display when declutter is selected.</P><P><span class='ad'><I>Click forward to&nbsp;view these&nbsp;items removed from the HUD.</P></I></span></span>]]></text><text bldnum=\"3\" ><![CDATA[&nbsp;<BR>]]></text><files mediaID=\"11230\" bldnum=\"1\" media=\"T38_SO1109_CBT_UFCPVOR3U4V.png\" media_x=\"525\" media_y=\"60\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"8041\" bldnum=\"2\" media=\"T38_SO1109_CBT_JMPS_declutter_page3_p49623_b8.jpg\" media_x=\"515\" media_y=\"90\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11661\" bldnum=\"3\" media=\"T38_SO1109_CBT_HUD_Declutter_b9.mp4\" media_x=\"466\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"1604\" bldnum=\"1\" type=\"rectangle\" x1=\"576\" y1=\"249\" x2=\"44\" y2=\"44\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"4208\" bldnum=\"2\" type=\"rectangle\" x1=\"525\" y1=\"160\" x2=\"235\" y2=\"310\" htext=\"\" colorType=\"1\" /></frame><frame id=\"821\" num=\"11\" revision=\"1.0\" tempType=\"0\" quesType=\"tf\" randomize=\"0\" title=\"End of Topic\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"End of Topic\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"990\" txtHeight=\"632\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<span class='an'>This concludes the </span><span class='ae'>Display and Controls </span><span class='an'>topic. The next topic will cover the </span><span class='ae'>Navigation Mode Display</span><span class='an'>.</span> <BR>]]></text><files mediaID=\"11552\" bldnum=\"1\" media=\"T38_SO1109_CBTsplashjetHUDflag_b9.png\" media_x=\"30\" media_y=\"125\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /></frame></topic><topic id=\"53\" num=\"4\" title=\"NAV Mode Display\"  type=\"0\"><frame id=\"t53\" num=\"1\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" title=\"Heads-Up Display (HUD)\" attachment=\"\" headphoneIcon=\"None\" menutitle=\"Topic Intro\" template=\"topicIntro\" numbuilds=\"1\" remediation=\"\" classlevel=\"0\" txtWidth=\"1000\" txtHeight=\"800\" txtLeft=\"150\" txtTop=\"0\" columnA=\"\" columnB=\"\" ><text bldnum=\"1\"><![CDATA[<span class='tn'><span class='th'>&emsp;HUD Symbology</span><br>&emsp;&emsp;- Lesson Objectives<br>&emsp;&emsp;- Introduction<br>&emsp;&emsp;- Display and Controls<br><span class='th'>&emsp;&emsp;- NAV Mode Display</span><br>&emsp;&emsp;- TCAS Display<br>]]></text></frame><frame id=\"828\" num=\"2\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"1\" title=\"Navigation Mode Display Introduction\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Introduction\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"459\" txtHeight=\"624\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The discussion of the F-16 HUD emulation will focus on the </span><span class='ae'>NAV master mode display</span><span class='an'>. This is the basic HUD display mode you will use on every SUPT mission for normal navigation and flight reference.</span></P><P><span class='an'></span><span class='an'>The specific flight symbology displayed on the HUD is determined by the primary navigation source <span class='an'>(PNS) selected </span></span><span class='an'>and the position of the landing gear. The data display changes to support each segment of flight from takeoff to landing.</span> <BR></P>]]></text><files mediaID=\"11729\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /></frame><frame id=\"829\" num=\"3\" revision=\"1.0\" tempType=\"0\" quesType=\"tf\" randomize=\"0\" title=\"Basic NAV Master Mode Display\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Basic Display\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"449\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The </span><span class='an'>basic NAV master mode display </span><span class='an'>consists of the following symbology and data blocks:</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Boresight Cross</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Flight Path Marker (FPM)</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Pitch Ladder</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Heading Scale, Indicator, Commanded<BR></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Heading</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Bank Scale and Arrow</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Velocity Scale and Indicator</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Altitude Scale and Indicator</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Ground Target Designator Box</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Bearing Arrow / Radial Readout</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Course Deviation Indicator (CDI)</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Flight Director (FD)</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Data Blocks and Windows</span></P><P><span class='an'></span><span class='an'>Each of these items is covered next, beginning with the </span><span class='an'>Boresight Cross</span><span class='an'>.</span> <BR></P>]]></text><files mediaID=\"11730\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /></frame><frame id=\"831\" num=\"4\" revision=\"1.0\" tempType=\"0\" quesType=\"tf\" randomize=\"0\" title=\"Boresight Cross\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Boresight Cross\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"447\" txtHeight=\"472\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The<span class='an'>&nbsp;Boresight Cross represents the aircraft &quot;water line&quot; and is often called the &quot;Gun Cross&quot;</span></span><span class='an'> since it is also used as a reference for the simulated gun boresight. </span></P><P><span class='an'>The symbol is centered laterally in the display and fixed in a position 3</span><span class='an'>°</span> <span class='an'>above the optical center of the HUD.</span> </P>]]></text><files mediaID=\"11731\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"1606\" bldnum=\"1\" type=\"rectangle\" x1=\"726\" y1=\"225\" x2=\"45\" y2=\"45\" htext=\"\" colorType=\"1\" /></frame><frame id=\"842\" num=\"5\" revision=\"1.0\" tempType=\"1\" quesType=\"tf\" randomize=\"0\" title=\"Flight Path Marker (FPM)\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Flight Path Marker\" template=\"NotUsed\" numbuilds=\"3\" remediation=\"0\" classlevel=\"0\" txtWidth=\"443\" txtHeight=\"543\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'><span class='an'>The <span class='an'>Flight Path Marker </span>(<span class='an'>FPM</span>) indicates </span></span><span class='an'>the velocity vector of the aircraft, or in other words, where the aircraft is actually going. During EGI alignment or in situations where the FPM reaches the display boundaries, an &quot;</span><span class='an'><B>X</B></span><span class='an'>&quot;<B> </B>appears in the center of the FPM as a visual cue that it is </span><span class='ae'><I><B><span class='an'>not</span> </B></I></span><span class='an'>displaying the actual velocity vector.</span></P><P><span class='an'></span><span class='ad'><I>Click the Forward button to see the &quot;X&quot; display for the FPM.</I></span> </P>]]></text><text bldnum=\"2\" ><![CDATA[<P><span class='an'>You can <span class='an'><b>limit the left-right movement </b></span></span><span class='an'>of the FPM by selecting <span class='an'>drift cut-out </span>(</span><span class='an'><b>DCO</b></span><span class='an'>) on the UFCP. When you select DCO, the FPM remains centered laterally on the HUD and only moves up and down indicating the vertical vector, or pitch. The related flight symbology remains &quot;tied&quot; to the FPM. The DCO feature is useful in high crosswind conditions to keep the flight control symbology in full view on the HUD.</span></P><P><span class='an'></span><span class='ad'><I>Click the Forward button to see the left-right movement of the FPM and &quot;tied&quot; symbology.</I></span> </P>]]></text><text bldnum=\"3\" ><![CDATA[&nbsp;<br>]]></text><files mediaID=\"11732\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"8050\" bldnum=\"2\" media=\"T38_SO1109_CBT_symbolsHUD_FPM X_P46091.png\" media_x=\"155\" media_y=\"555\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11795\" bldnum=\"2\" media=\"T38_SO1109_CBT_HUD-VORCDI-FPMX_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11662\" bldnum=\"3\" media=\"T38_SO1109_CBT_HUD_DCODRF_b9.mp4\" media_x=\"465\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"1610\" bldnum=\"1\" type=\"rectangle\" x1=\"723\" y1=\"295\" x2=\"54\" y2=\"44\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"3336\" bldnum=\"2\" type=\"rectangle\" x1=\"724\" y1=\"296\" x2=\"54\" y2=\"44\" htext=\"\" colorType=\"1\" /></frame><frame id=\"886\" num=\"6\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"1\" title=\"Pitch Ladder #1\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Pitch Ladder #1\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"447\" txtHeight=\"462\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The<span class='an'>&nbsp;HUD pitch ladder </span></span><span class='an'>is roll stabilized and laterally attached to the FPM. The pitch ladder displays the climb/dive angle when read relative to the FPM and consists of the following symbols:</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Horizon Line</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Climb / Dive Bars</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Zenith / Nadir Symbol</span> <BR></P>]]></text><files mediaID=\"11733\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /></frame><frame id=\"1214\" num=\"7\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Pitch Ladder #2\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Pitch Ladder #2\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"446\" txtHeight=\"457\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='ae'><I><B>HORIZON LINE</B></I></span></P><P><span class='ae'></span><span class='an'>The horizon line extends across the total HUD FOV. When the horizon line moves outside of the HUD field of view at the higher pitch attitudes, a </span><span class='ae'>Ghost Horizon Line</span> <span class='an'>(</span><span class='an'>GHL</span><span class='an'>) is displayed as a reference. The GHL is displayed as a dashed line near the outer edge of the HUD display, parallel to the actual horizon.</span> <BR></P>]]></text><files mediaID=\"11734\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"1832\" bldnum=\"1\" type=\"rectangle\" x1=\"573\" y1=\"287\" x2=\"350\" y2=\"25\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1236\" num=\"8\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Pitch Ladder #3\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Pitch Ladder #3\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"446\" txtHeight=\"460\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='ae'><I><B>CLIMB / DIVE BARS</B></I></span></P><P><span class='ae'></span><span class='an'>The climb/dive bars are displayed in 5</span><span class='an'>°</span> <span class='an'>intervals from +85</span><span class='an'>°</span> <span class='an'>to -85</span><span class='an'>°</span> <span class='an'>and are labeled at each outer end. The climb bars are solid and have outer elbows that point toward the horizon.</span></P><P><span class='an'></span><span class='an'>The dive bars are dashed and slope downward at an angle that is approximately </span><span class='an'>½ </span><span class='an'>the dive angle. The dive bars have elbows on the inner end that also point toward the horizon.</span></P><P><span class='an'></span><span class='an'>The exception is the 2.5</span><span class='an'>°</span> <span class='an'>dive reference line that appears when the gear is down (straight dashed line with no elbows).</span> <BR></P>]]></text><files mediaID=\"11735\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"1834\" bldnum=\"1\" type=\"rectangle\" x1=\"673\" y1=\"175\" x2=\"155\" y2=\"35\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"1835\" bldnum=\"1\" type=\"rectangle\" x1=\"673\" y1=\"385\" x2=\"155\" y2=\"35\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1243\" num=\"9\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Pitch Ladder #4\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Pitch Ladder #4\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"446\" txtHeight=\"460\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='ae'><I><B>ZENITH / NADIR SYMBOL</B></I></span></P><P><span class='ae'></span><span class='an'>The Zenith / Nadir symbols are displayed as circles on the F-16 HUD pitch ladder. The center of the circle represents the 90</span><span class='an'>°</span> <span class='an'>climb/dive angle.</span></P><P><span class='an'></span><span class='an'>Notice the </span><span class='ad'><I>Ghost Horizon Line</I></span> <span class='an'><I></I>positioned near the bottom of the display.</span></P><P><span class='an'></span><span class='ad'><I>Click on &quot;Ghost Horizon Line&quot; text above to highlight the GHL.</I></span> <BR></P>]]></text><files mediaID=\"11692\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD_Zenith_b9.mp4\" media_x=\"466\" media_y=\"55\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><hotspot hotSpotID=\"347\" bldNum=\"1\" actionType=\"Display a File\" activateOn=\"Click\" action=\"T38_SO1109_CBT_symbolsHUD_yellow_rect_P46096_hs347.png\" displayType=\"Invisible\" left=\"92\" top=\"114\" width=\"180\" height=\"30\" mediaX=\"527\" mediaY=\"508\" textWidth=\"100\" textHeight=\"100\" dropShadow=\"0\" /></frame><frame id=\"935\" num=\"10\" revision=\"1.0\" tempType=\"1\" quesType=\"mcT\" randomize=\"0\" title=\"Heading Scale\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Heading Scale\" template=\"NotUsed\" numbuilds=\"2\" remediation=\"0\" classlevel=\"0\" txtWidth=\"429\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>You read the aircraft's magnetic heading on a scale below the HUD fixed heading indicator line, and digitally displayed in the rectangular box (e.g., 180). The scale shows </span><span class='an'>±</span><span class='an'>10</span><span class='an'>°</span> <span class='an'>of the present heading in 5</span><span class='an'>°</span> <span class='an'>increments. Every 10</span><span class='an'>°</span> <span class='an'>is labeled with a 2-digit number. The &quot;Captains bars&quot; mark the manual heading selected on the UFCP/MFD when it is within the heading scale range. The marker is not displayed if the set heading is outside the scale limits.</span></P><P><span class='an'></span><span class='an'>The heading scale is displayed in either the </span><span class='ae'><span class='an'><I>upper or lower </I></span></span><span class='an'>part of the HUD depending on the selected avionics mode and position of the landing gear lever. It moves to provide a more unobstructed view of the primary symbology and visual references. The heading scale is at the top of the HUD:</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;In Air-to-Ground Master Mode</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;In the Air-to-Air Master Mode</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;With the landing gear lever down</span></P><P><span class='an'></span><span class='ad'><I>Click the Forward button to see the Heading Scale in lower display.</I></span></P>]]></text><text bldnum=\"2\" ><![CDATA[<P><span class='an'>In all other modes, it is in the bottom of the display.</P></span>]]></text><files mediaID=\"11736\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI5pt0AAT-AOAsym_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11737\" bldnum=\"2\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"472\" media_y=\"51\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"3541\" bldnum=\"2\" type=\"line\" x1=\"726\" y1=\"394\" x2=\"759\" y2=\"394\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"3542\" bldnum=\"2\" type=\"line\" x1=\"758\" y1=\"395\" x2=\"758\" y2=\"411\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"3543\" bldnum=\"2\" type=\"line\" x1=\"757\" y1=\"412\" x2=\"829\" y2=\"412\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"3544\" bldnum=\"2\" type=\"line\" x1=\"828\" y1=\"413\" x2=\"828\" y2=\"451\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"3545\" bldnum=\"2\" type=\"line\" x1=\"829\" y1=\"452\" x2=\"651\" y2=\"452\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"3546\" bldnum=\"2\" type=\"line\" x1=\"652\" y1=\"450\" x2=\"652\" y2=\"412\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"3547\" bldnum=\"2\" type=\"line\" x1=\"653\" y1=\"412\" x2=\"726\" y2=\"412\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"3548\" bldnum=\"2\" type=\"line\" x1=\"727\" y1=\"395\" x2=\"727\" y2=\"413\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"5918\" bldnum=\"1\" type=\"rectangle\" x1=\"669\" y1=\"167\" x2=\"160\" y2=\"52\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1002\" num=\"11\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Bank Scale\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Bank Scale\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"451\" txtHeight=\"545\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<span class='an'>The bank scale display at the bottom of the HUD consists of the following components:</span> <BR>]]></text><files mediaID=\"11685\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD_Bank_b9_982_669.swf\" media_x=\"20\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /></frame><frame id=\"1346\" num=\"12\" revision=\"1.0\" tempType=\"0\" quesType=\"tf\" randomize=\"0\" title=\"Velocity Scale #1\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Velocity Scale #1\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"457\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The </span><span class='ae'>airspeed display </span><span class='an'>consists of the following elements:</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Velocity Scale</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Indicator and Type</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Digital Velocity Readout</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Commanded Velocity Indicators (<span class='an'>CVI</span>)</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Time of Arrival (TOA) Indicators</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Divert Mode Display</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Speedbrake Indication</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Minimum Mach Indicator</span> </P>]]></text><files mediaID=\"11738\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"1955\" bldnum=\"1\" type=\"rectangle\" x1=\"600\" y1=\"255\" x2=\"70\" y2=\"155\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1481\" num=\"13\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Velocity Scale #2\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Velocity Scale #2\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"451\" txtHeight=\"635\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='ae'><I><B>VELOCITY SCALE</B></I></span> </P><P><span class='an'>The vertical tape scale continuously displays 80 knots above and below the current velocity in 10-knot increments. Every 50 knots on the scale is marked by a longer line with a digital label. The velocity labels consist of one or two digits indicating tens of knots (&quot;5&quot; = 50, &quot;10&quot; = 100, and &quot;15&quot; = 150).</span> <BR></P>]]></text><files mediaID=\"11739\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"1956\" bldnum=\"1\" type=\"rectangle\" x1=\"630\" y1=\"255\" x2=\"25\" y2=\"155\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1508\" num=\"14\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"1\" title=\"Velocity Scale #3\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Velocity Scale #3\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"449\" txtHeight=\"623\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='ae'><I><B>VELOCITY INDICATOR AND TYPE</B></I></span></P><P><span class='ae'></span><span class='an'>The current velocity is marked by a short horizontal line indicator fixed at the center of the scale. The letter above this line indicates the type of velocity displayed on the scale. The different speed types selectable via the UFCP are; &quot;<span class='ae'>C</span>&quot; calibrated (CAS), &quot;<span class='ae'>T</span>&quot; true (TAS), and &quot;<span class='ae'>G</span>&quot; ground speed (GS). CAS is the normal NAV master mode default displayed airspeed and the default speed when the landing gear lever is down.</span> <BR></P>]]></text><files mediaID=\"11740\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"1958\" bldnum=\"1\" type=\"rectangle\" x1=\"641\" y1=\"309\" x2=\"24\" y2=\"28\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1514\" num=\"15\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"1\" title=\"Velocity Scale #4\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Velocity Scale #4\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"450\" txtHeight=\"624\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='ae'><I><B>DIGITAL VELOCITY READOUT</B></I></span></P><P><span class='ae'></span><span class='an'>The current velocity is digitally displayed to the nearest knot in the box at the center of the scale. Up to three digits are displayed with no leading zeros. As the scale numbers move up or down through the digital readout box, they are occluded so as not to interfere with this display. </span></P><P><span class='an'>The speed indicates 000 below about 30 knots. </span></P><P><span class='an'>The display is blank if there is no airspeed data available. </span><BR></P>]]></text><files mediaID=\"11741\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"1960\" bldnum=\"1\" type=\"rectangle\" x1=\"603\" y1=\"314\" x2=\"40\" y2=\"30\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1515\" num=\"16\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"1\" title=\"Velocity Scale #5\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Velocity Scale #5\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"444\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='ae'><I><B>COMMANDED VELOCITY INDICATORS</B></I></span> <span class='an'></span><BR><span class='an'></span>&nbsp;<BR><span class='an'>The manual CVI caret or &quot;bug&quot; indicates the green speed manually set on the UFCP by the pilot. The TOT/TTT CVI indicates the MDP-computed Time-of-Arrival (TOA) speed required to reach steer points with a valid predefined TOT/TTT. If the MDP generates a TOT/TTT CVI, the manual CVI is removed. </span></P><P><span class='an'>If the CVI is outside of the display limits, the marker is fixed at the top or bottom of the scale until the speed is within the display range. </span></P><P><span class='an'>When the gear is down or the Divert Mode is active, the manual CVI speed bug is displayed.</span> <BR></P>]]></text><files mediaID=\"11695\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD_SpeedDisplay_b9.mp4\" media_x=\"465\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"3549\" bldnum=\"1\" type=\"rectangle\" x1=\"647\" y1=\"345\" x2=\"45\" y2=\"24\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"3550\" bldnum=\"1\" type=\"rectangle\" x1=\"625\" y1=\"410\" x2=\"39\" y2=\"23\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"5213\" bldnum=\"1\" type=\"rectangle\" x1=\"808\" y1=\"511\" x2=\"79\" y2=\"27\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1521\" num=\"17\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Velocity Scale #6\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Velocity Scale #6\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"450\" txtHeight=\"624\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='ae'><I><B>DIVERT MODE</B></I></span> </P><P><span class='an'>When you select&nbsp;a Divert Mode via the UFCP; the system displays&nbsp;</span><span class='ae'>CLM</span><span class='an'>, </span><span class='ae'>CRZ</span><span class='an'>, </span><span class='ae'>RNG</span><span class='an'>, or </span><span class='ae'>END </span><span class='an'>markers on the velocity scale (if speeds are on the scale) for the active divert mode. The CLM (climb) marker is only displayed when required for the divert profile. The CRZ (cruise) label changes to<span class='ae'>&nbsp;DES </span>(descend</span><span class='an'>) when you enter the descent phase of the divert profile.</span></P><P><span class='an'></span><span class='an'>A &quot;</span><span class='aalt'><span class='an'>DESCEND</span><span class='an'>&quot;</span> </span><span class='an'>alert message flashes on the HUD for five seconds when descent conditions are met. Pressing the ACK key removes this caution.</span> <BR></P>]]></text><files mediaID=\"11742\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-CrzClmDesc10pt3_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"1969\" bldnum=\"1\" type=\"rectangle\" x1=\"601\" y1=\"226\" x2=\"91\" y2=\"183\" htext=\"\" colorType=\"1\" /></frame><frame id=\"3305\" num=\"18\" revision=\"1.0\" tempType=\"1\" quesType=\"mcT\" randomize=\"0\" title=\"Velocity Scale #7\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Velocity Scale #7\" template=\"NotUsed\" numbuilds=\"4\" remediation=\"0\" classlevel=\"0\" txtWidth=\"442\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='ae'><I><B>TIME-OF-ARRIVAL (TOA) INDICATORS</B></I></span></P><P><span class='ae'></span><span class='aalt'>TOT Display for a Destination <span class='aalt'>Point </span><span class='an'><span class='an'>—</span> <B></B>If </span></span><span class='an'>a steerpoint with a valid time-over-target (TOT) is selected and TOT mode is active, the chronometer is replaced with a boxed, <i>constant</i> value TOT time (HH:MM:SS) display.</span> </P>]]></text><text bldnum=\"2\" ><![CDATA[<P><span class='aalt'>TTT Display for a Destination <span class='aalt'>Point</span><span class='an'><B> </B><span class='an'>—</span> <B></B>If </span></span><span class='an'>a steerpoint with a valid time-to-target (TTT) is selected and the TTT mode is active, the chronometer is replaced by a boxed &quot;MM:SS&quot; <i>countdown</i> format. When the chronometer is hacked, it resets to the TTT time instead of zero.</span> </P>]]></text><text bldnum=\"3\" ><![CDATA[<P><span class='aalt'>TOT Display for FPL <span class='aalt'>Waypoint</span><span class='an'><B> </B><span class='an'>—</span> <B></B>If </span></span><span class='an'>a Flight Plan Point with a valid TOT is the steerpoint and TOT mode is active, the chronometer is replaced with a boxed </span><span class='an'>&quot;##W MM:SS&quot; <I>countdown</I> TOT format.</span> </P>]]></text><text bldnum=\"4\" ><![CDATA[<span class='aalt'>TTT Display for FPL<span class='an'><span class='aalt'>&nbsp;Waypoint</span><B> </B><span class='an'>—</span> <B></B>If </span></span><span class='an'>a Flight Plan waypoint with a valid TTT is the next steerpoint and the TTT mode is active, the chronometer is replaced by a boxed </span><span class='an'>&quot;##W MM:SS&quot; <I>countdown</I> format. If you hack the chronometer in this mode, it resets to the TTT time instead of zero.</span><BR>]]></text><files mediaID=\"11744\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VOR83pt8MMTOTx2_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11796\" bldnum=\"2\" media=\"T38_SO1109_CBT_HUD-VOR83pt8MMTTTx2_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11745\" bldnum=\"3\" media=\"T38_SO1109_CBT_HUD-VOR83pt8MMTOTx2-03W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11808\" bldnum=\"4\" media=\"T38_SO1109_CBT_HUD-VOR83pt8MMTTTx2-03W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"5165\" bldnum=\"1\" type=\"rectangle\" x1=\"818\" y1=\"488\" x2=\"80\" y2=\"30\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"5210\" bldnum=\"2\" type=\"rectangle\" x1=\"831\" y1=\"488\" x2=\"59\" y2=\"30\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"5211\" bldnum=\"3\" type=\"rectangle\" x1=\"823\" y1=\"488\" x2=\"80\" y2=\"30\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"5212\" bldnum=\"4\" type=\"rectangle\" x1=\"823\" y1=\"488\" x2=\"80\" y2=\"30\" htext=\"\" colorType=\"1\" /></frame><frame id=\"3304\" num=\"19\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Velocity Scale #8\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Velocity Scale #8\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"442\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='ae'><I><B>MINIMUM MACH INDICATOR</B></I></span></P><P><span class='ae'></span><span class='an'>Above 28,000 feet corrected MSL altitude, a minimum Mach (&quot;</span><span class='ae' ?'>–MM</span></span><span class='an'>&quot;) CVI airspeed marker is displayed on the velocity scale of the HUD. The ADC converts the minimum mach determined from the <I>Engine Compressor Stall / Flameout, Susceptibility Areas </I>graph in the Flight Manual to the appropriate airspeed displayed on the scale (i.e., calibrated, true, ground).</span> <BR></P>]]></text><files mediaID=\"11747\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VOR83pt8MMTOTx2_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"4782\" bldnum=\"1\" type=\"rectangle\" x1=\"632\" y1=\"333\" x2=\"60\" y2=\"20\" htext=\"\" colorType=\"1\" /></frame><frame id=\"2861\" num=\"20\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Velocity Scale #9\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Velocity Scale #9\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"441\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='ae'><I><B>SPEEDBRAKE INDICATION</B></I></span> </P><P><span class='aalt'>(After TCTO 1T-38C-551)</span> <span class='an'>When the speedbrake is extended, a flashing &quot;</span><span class='ae'>SB</span><span class='an'>&quot; is displayed just to the left of the current airspeed readout.</span> <BR></P>]]></text><files mediaID=\"11723\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD_SBind_b9.mp4\" media_x=\"485\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"4160\" bldnum=\"1\" type=\"rectangle\" x1=\"587\" y1=\"300\" x2=\"17\" y2=\"32\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1537\" num=\"21\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Altitude Scale #1\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Altitude Scale #1\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"453\" txtHeight=\"624\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The </span><span class='ae'>altitude display </span><span class='an'>consists of the following elements:</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Altitude Scale</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Altitude Marker</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Digital Altitude Readout</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Divert Mode Display</span> <BR><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp; </span><BR></P>]]></text><files mediaID=\"11748\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"1990\" bldnum=\"1\" type=\"rectangle\" x1=\"840\" y1=\"256\" x2=\"75\" y2=\"145\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1581\" num=\"22\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Altitude Scale #2\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Altitude Scale #2\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"448\" txtHeight=\"637\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='ae'><I><B>BAROMETRIC ALTITUDE SCALE</B></I></span></P><P><span class='ae'></span><span class='an'>The </span><span class='ae'>vertical tape scale </span><span class='an'>displays 800 feet above and below the current altitude in 100-feet increments. Every 500 feet on the scale is marked with a longer line and a digital altitude label. The 500-foot labels consist of one or two digits that indicate thousands of feet, followed by a comma and single digit (either 0 or 5) indicating hundreds of feet.</span></P><P><span class='an'></span><span class='an'>Examples:</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;0,5 = 500 feet</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;15,0 = 15,000 feet</span> <BR></P>]]></text><files mediaID=\"11749\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"1991\" bldnum=\"1\" type=\"rectangle\" x1=\"838\" y1=\"256\" x2=\"25\" y2=\"145\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1592\" num=\"23\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Altitude Scale #3\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Altitude Scale #3\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"447\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='ae'><I><B>ALTITUDE MARKER</B></I></span></P><P><span class='ae'></span><span class='an'>The </span><span class='ae'>current barometric altitude </span><span class='an'>is marked by a short horizontal line fixed at the center of the scale.</span> <BR></P>]]></text><files mediaID=\"11750\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"2052\" bldnum=\"1\" type=\"rectangle\" x1=\"837\" y1=\"319\" x2=\"20\" y2=\"20\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1624\" num=\"24\" revision=\"1.0\" tempType=\"1\" quesType=\"mcT\" randomize=\"0\" title=\"Altitude Scale #4\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Altitude Scale #4\" template=\"NotUsed\" numbuilds=\"2\" remediation=\"0\" classlevel=\"0\" txtWidth=\"443\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='ae'><I><B>DIGITAL ALTITUDE READOUT</B></I></span></P><P><span class='ae'></span><span class='an'>The </span><span class='ae'>current altitude <span class='an'>is digitally displayed </span></span><span class='an'>to the nearest 10 feet in the rectangular box at the center of the altitude scale. The readout displays up to five digits with no leading zeros, except when the altitude is zero (000 displayed). As the altitude scale numbers move up or down through the digital readout, the numbers are occluded by the box so as not to interfere with the altitude readout display. </span></P><P><span class='an'>If altitude data is not available, there is no digital display.</span> </P>]]></text><text bldnum=\"2\" ><![CDATA[<P><span class='an'>When you select the QNE altimeter (29.92), the box around the current altitude </span><span class='ae'>flashes </span><span class='an'>continuously if:</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;The RALT altitude is below 5,000 feet or,</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;The Baro altitude is less than 5,000 feet if<BR></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;RALT is invalid or,</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;The Baro altitude is less than 8,000 feet if<BR></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;RALT is OFF.</span></P><P><span class='an'></span><span class='an'>Selecting a QNH altimeter setting (not 29.92) stops the display from flashing.</span> <BR></P>]]></text><files mediaID=\"11751\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11724\" bldnum=\"2\" media=\"T38_SO1109_CBT_HUD_ALTFlash_b9.mp4\" media_x=\"465\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"3670\" bldnum=\"1\" type=\"rectangle\" x1=\"862\" y1=\"316\" x2=\"50\" y2=\"25\" htext=\"\" colorType=\"1\" /></frame><frame id=\"2863\" num=\"25\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"1\" title=\"Altitude Scale #5\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Altitude Scale #5\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"441\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='ae'><I><B>DIVERT MODE</B></I></span></P><P><span class='ae'></span><span class='an'>When you select&nbsp;a divert mode on the UFCP, an <span class='ae'>ALT&nbsp;</span>marker (altitude</span><span class='an'>) is displayed on the scale (if the altitude is on the scale) for the active divert mode.</span></P><P><span class='an'></span><span class='an'>The distance to the descent point for the divert field (&quot;</span><span class='ae'><span class='an'>DESC&gt;10.3&quot;) </span></span><span class='an'>is displayed above the TTG in the NAV master mode.</span> <BR></P>]]></text><files mediaID=\"11752\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-CrzClmDesc10pt3_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"3664\" bldnum=\"1\" type=\"rectangle\" x1=\"814\" y1=\"255\" x2=\"100\" y2=\"150\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"3665\" bldnum=\"1\" type=\"rectangle\" x1=\"840\" y1=\"441\" x2=\"75\" y2=\"20\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1632\" num=\"26\" revision=\"1.0\" tempType=\"1\" quesType=\"tf\" randomize=\"0\" title=\"Target Designator (TD) Box\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"TD Box\" template=\"NotUsed\" numbuilds=\"3\" remediation=\"0\" classlevel=\"0\" txtWidth=\"442\" txtHeight=\"629\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The </span><span class='ae'>Target Designator (TD) box </span><span class='an'>(diamond) indicates the computed position of the current steer point on the ground, based on the point coordinates. If the point has a defined elevation, you should see the steer point located inside the TD box when it is within the HUD <span class='an'>FOV</span>. For example, if the steer point is a railroad bridge, you should see the bridge through the TD box when you are in visual range.</span> </P>]]></text><text bldnum=\"2\" ><![CDATA[<P><span class='an'>If the <span class='an'><i>point is outside of the TD box display limits</i></span></span><span class='an'>, the TD box is positioned in the general direction of the point at the edge of the display. Whenever this occurs, an &quot;</span><span class='ae'><B>X</B></span><span class='an'>&quot;<B> </B>symbol is displayed over the TD box.</span> </P>]]></text><text bldnum=\"3\" ><![CDATA[<P><span class='an'>If a </span><span class='an'><i>defined elevation is not available </i></span><span class='an'>for a steer point (e.g., some ICAO points) the TD box is displayed on the horizon line.</span></P><P><span class='an'></span><span class='an'>Whenever the landing gear lever is down, the TD box is removed and not displayed except when destination points 500 through 599 are selected.</span> <BR></P>]]></text><files mediaID=\"11753\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11663\" bldnum=\"2\" media=\"T38_SO1109_CBT_HUD_TDBoxX_b9.mp4\" media_x=\"465\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11664\" bldnum=\"2\" media=\"T38_SO1109_CBT_HUD_TDBoxX2_b9.mp4\" media_x=\"185\" media_y=\"399\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11665\" bldnum=\"3\" media=\"T38_SO1109_CBT_HUD_TDBoxmove_b9.mp4\" media_x=\"465\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"2054\" bldnum=\"1\" type=\"rectangle\" x1=\"797\" y1=\"463\" x2=\"40\" y2=\"40\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"2055\" bldnum=\"2\" type=\"rectangle\" x1=\"784\" y1=\"485\" x2=\"40\" y2=\"40\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1665\" num=\"27\" revision=\"1.0\" tempType=\"1\" quesType=\"mcT\" randomize=\"0\" title=\"Bearing Arrow and Radial Readout\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Bearing/Radial\" template=\"NotUsed\" numbuilds=\"4\" remediation=\"0\" classlevel=\"0\" txtWidth=\"443\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The </span><span class='ae'>bearing arrow and radial readout </span><span class='an'>symbology provides a visual display of the aircraft's position relative to either the EGI steer point, the VOR, or the TACAN in the NAV master mode. If VOR or TCN is selected as the <span class='adef'><u><a href='event:PNS'>PNS</a></u></span>, the display shows the radial and bearing from the current VOR or TCN ground station (if received). In all other navigation modes, the positional data is in relation to the current EGI steerpoint.</span> </P>]]></text><text bldnum=\"2\" ><![CDATA[<P><span class='ae'><B>Bearing Arrow</B></span> <span class='aalt'></span><span class='an'>—</span> <span class='an'>Rotates 360</span><span class='an'>°</span> <span class='an'>around the digital readout to indicate relative bearing to the selected VOR, TCN ground station, or EGI point.</span> </P>]]></text><text bldnum=\"3\" ><![CDATA[<P><span class='ae'><B>Reference Wings</B></span> <span class='aalt'></span><span class='an'>—</span> <span class='an'>Always remain fixed in the horizontal.</span> </P>]]></text><text bldnum=\"4\" ><![CDATA[<P><span class='ae'><B>Radial Readout</B></span> <span class='aalt'></span><span class='an'>—</span> <span class='an'>The digital display also remains parallel to the wings and shows the radial you are on from the selected VOR, TCN ground station, or EGI steerpoint.</span></P><P><span class='an'></span><span class='an'>The system removes the entire display from the HUD if the display data or signal is lost.</span> <BR></P>]]></text><files mediaID=\"11754\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11755\" bldnum=\"2\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11756\" bldnum=\"3\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11757\" bldnum=\"4\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"2086\" bldnum=\"1\" type=\"rectangle\" x1=\"843\" y1=\"164\" x2=\"60\" y2=\"50\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"2087\" bldnum=\"2\" type=\"rectangle\" x1=\"870\" y1=\"166\" x2=\"26\" y2=\"29\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"2088\" bldnum=\"3\" type=\"rectangle\" x1=\"880\" y1=\"190\" x2=\"21\" y2=\"18\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"2089\" bldnum=\"3\" type=\"rectangle\" x1=\"846\" y1=\"190\" x2=\"21\" y2=\"18\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"2090\" bldnum=\"4\" type=\"rectangle\" x1=\"858\" y1=\"189\" x2=\"30\" y2=\"20\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1726\" num=\"28\" revision=\"1.0\" tempType=\"1\" quesType=\"mcT\" randomize=\"0\" title=\"Course Deviation Indicator (CDI)\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"CDI\" template=\"NotUsed\" numbuilds=\"2\" remediation=\"0\" classlevel=\"0\" txtWidth=\"442\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The <span class='ae'>Course Deviation Indicator </span>(<span class='an'>CDI</span>) shows deviation from the selected course in the NAV mode. The course arrow and dot scale </span><span class='an'>rotate</span> <span class='an'>around the <span class='an'>FPM</span> indicating the heading angle and relative position from the course. <span class='an'>When <span class='ae'>EGI</span> is the <span class='an'>PNS</span>, </span></span><span class='an'>CDI scaling is </span><span class='an'>based on system <span class='an'>RNP</span> </span><span class='an'>(</span><span class='ae'><span class='an'>2 dots&nbsp;= current RNP value</span></span><span class='ae'><span class='an'>)</span></span><span class='an'>.&nbsp;In <span class='ae'>VOR or TCN</span>, each dot represents 5<span class='an'>°</span> of deviation</span><span class='an'>. The CDI is </span><span class='an'>centered</span> <span class='an'>over the FPM when &quot;on course.&quot; No more than 2 dots are shown at any time.</span> <BR><BR></P>]]></text><text bldnum=\"2\" ><![CDATA[&nbsp;<br>]]></text><files mediaID=\"11672\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD_CDITurn_b9.mp4\" media_x=\"465\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11686\" bldnum=\"2\" media=\"T38_SO1109_CBT_HUD_CDIDev_b9_982_669.swf\" media_x=\"20\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /></frame><frame id=\"3368\" num=\"29\" revision=\"1.0\" tempType=\"1\" quesType=\"mcT\" randomize=\"1\" title=\"Flight Director (FD) Modes\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Flt Dir Modes\" template=\"NotUsed\" numbuilds=\"2\" remediation=\"0\" classlevel=\"0\" txtWidth=\"469\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>There are four (4) pilot-selectable </span><span class='ae'>flight director (FD) steering modes </span><span class='an'>available.</span></P><P><span class='an'></span><span class='an'>On the MFD, MT-1 acts as the toggle to select OFF (</span><span class='ae'>FD <span class='an'>and&nbsp;</span></span><span class='an'>unboxed), Heading-to-Course (</span><span class='ae'>H2C</span> <span class='an'>and boxed), Course (</span><span class='ae'>CRS</span> <span class='an'>and boxed) and Heading (</span><span class='ae'>HDG</span> <span class='an'>and boxed) in that order.</span></P><P><span class='an'></span><span class='ad'><I>Toggle through the FD modes by pressing MT-1 on the MFD.</I></span> </P>]]></text><text bldnum=\"2\" ><![CDATA[<P><span class='an'>You can also cycle through the FD modes on the UFCP. Press UL-1 on the </span><span class='ae'>UFCP Basic Display </span><span class='an'>page to access the </span><span class='ae'>Nav Sub-Menu </span><span class='an'>display.</span></P><P><span class='an'></span><span class='ad'><I>Toggle through the FD modes by pressing UL-4 on the UFCP.</I></span> <BR><span class='ad'></span>&nbsp;<BR></P>]]></text><files mediaID=\"11673\" bldnum=\"1\" media=\"T38_SO1109_CBT_MFD_FDStates_b9_440_568.swf\" media_x=\"540\" media_y=\"80\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11674\" bldnum=\"2\" media=\"T38_SO1109_CBT_UFCP_FDStates_b9_447_504.swf\" media_x=\"510\" media_y=\"60\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /></frame><frame id=\"1742\" num=\"30\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Flight Director #1\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Flight Director #1\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"495\" txtHeight=\"635\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<span class='an'>The<span class='an'>&nbsp;FD </span></span><span class='an'>defaults to CRS when you select&nbsp;ILS, LOC, BC, or SCA as the primary navigation source. Otherwise, the FD defaults to OFF when either the EGI, VOR, or TCN navigation mode is selected.</span> <BR>]]></text><files mediaID=\"11598\" bldnum=\"1\" media=\"T38_SO1109_CBT_MFDHSDSCA1-E3pt7-23sec_b9.png\" media_x=\"540\" media_y=\"80\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"5590\" bldnum=\"1\" type=\"rectangle\" x1=\"628\" y1=\"81\" x2=\"45\" y2=\"65\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1739\" num=\"31\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Flight Director #2\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Flight Director #2\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"442\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<span class='an'>With a FD mode selected on the UFCP or MFD, steering commands are provided on the HUD and EADI. The FD provides </span><span class='an'><I>bank steering only&nbsp;</I></span><span class='an'>(horizontal commands) </span><span class='an'>as shown in the example, or <I>both p</I></span><span class='an'><I>itch and bank steering </I></span><span class='an'>(horizontal&nbsp;and vertical commands). This is covered later on the ILS display.</span> <BR><span class='an'></span>&nbsp;<BR>]]></text><files mediaID=\"11758\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-103W_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"3692\" bldnum=\"1\" type=\"rectangle\" x1=\"764\" y1=\"287\" x2=\"25\" y2=\"25\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1820\" num=\"32\" revision=\"1.0\" tempType=\"1\" quesType=\"mcT\" randomize=\"1\" title=\"Flight Director #3\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Flight Director #3\" template=\"NotUsed\" numbuilds=\"2\" remediation=\"0\" classlevel=\"0\" txtWidth=\"444\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<P><span class='ag'>With a FD mode selected on the UFCP or MFD, steering commands are provided on the HUD and EADI. The FD provides <i>bank steering only&nbsp;</i>(horizontal commands) as shown in the example, or <i>both pitch and bank steering </i>(horizontal&nbsp;and vertical commands). This is covered later on the ILS display.</span></P><P><span class='ag'></span><span class='ae'><I><B>BANK STEERING ONLY</B></I></span></P><P><span class='ae'></span><span class='an'>When you select a FD mode on the UFCP or MFD, the bank steering circle provides roll commands for up to 30</span><span class='an'>°</span> <span class='an'>of bank. </span><span class='ae'>FD steering </span><span class='an'>is available when LOC, BC, VOR, TCN, or EGI is selected for navigation, or in the SCA/FIP nav mode. The </span><span class='ae'>FD circle moves left/right </span><span class='an'>across the horizon line indicating roll direction and required amount of bank to intercept and maintain the selected course. You should adjust the bank to keep the circle centered with the FPM.</span></P><P><span class='an'></span><span class='ad'><I>Click the forward button to see how the FD moves in relation to the FPM.</I></span> <BR></P>]]></text><text bldnum=\"2\" ><![CDATA[&nbsp;<BR>]]></text><files mediaID=\"11759\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-138-22secNoFIREwarn2_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11675\" bldnum=\"2\" media=\"T38_SO1109_CBT_HUD_CDIRange_b9.mp4\" media_x=\"465\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"3699\" bldnum=\"1\" type=\"rectangle\" x1=\"764\" y1=\"287\" x2=\"25\" y2=\"25\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1837\" num=\"33\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"CDI Comparison\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"CDI Comparison\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"990\" txtHeight=\"118\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<span class='an'>Compare the corresponding displays on the HUD and MFD below. Note how the CDI, FD, and <span class='an'>PNS</span> data (i.e., EGI) is presented on both the MFD and HUD. The display formats are somewhat different, but the information is the same. With EGI as the <span class='an'>PFR</span> in this example, the aircraft is displaced about 1/2 the RNP value (2.5 NM)&nbsp;right of the 040</span><span class='an'>°</span> <span class='an'>course, heading 180</span><span class='an'>°</span><span class='an'>. </span><BR>]]></text><files mediaID=\"11760\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUDMFD-EGI1pt8CRS04023sec035brg_b9.png\" media_x=\"35\" media_y=\"170\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /></frame><frame id=\"1861\" num=\"34\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Data Blocks\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Data Blocks\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"457\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>These additional </span><span class='an'>data blocks </span><span class='an'>and information are displayed on the HUD:</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Bullseye Bearing and Range</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Avionics Mode</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Maximum G-Load</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Current Mach Number</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Weapons Delivery Status</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Current G-Load</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Current AOA</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Course and Range</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Fuel Data Block</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;RALT Readout</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Minimum Radar Altitude/Alert</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;EGI Data Block / AAT Range</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Message Window</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Time Indicators</span></P><P><span class='an'></span><span class='an'>Each of these data displays is covered in the following screens.</span> <BR></P>]]></text><files mediaID=\"11761\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-138-22secNoFIREwarn2_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /></frame><frame id=\"1877\" num=\"35\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"1\" title=\"Bullseye\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Bullseye\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"454\" txtHeight=\"565\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The </span><span class='ae'>Bullseye </span><span class='an'>mode provides bearing and range from any selected destination or ICAO point to your aircraft when enabled on the UFCP. The Bullseye mode and point are selected on the </span><span class='an'>UFCP Bullseye </span><span class='an'>sub-menu. Bearing and range are displayed in a BXXX/XXX format with bearing listed first.&nbsp;&nbsp;The Bullseye display label is outside of the HUD IFOV.</span></P><P><span class='an'></span><span class='an'>The </span><span class='ae'>Bullseye symbol </span><span class='an'>is displayed on the MFD HSD/SIT displays when the point is within range. </span><BR></P>]]></text><files mediaID=\"11762\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-138-22secNoFIREwarn2_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11763\" bldnum=\"1\" media=\"T38_SO1109_CBT-UFCPsmBullpage-369_b4.png\" media_x=\"95\" media_y=\"385\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"2396\" bldnum=\"1\" type=\"rectangle\" x1=\"593\" y1=\"490\" x2=\"60\" y2=\"25\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"2397\" bldnum=\"1\" type=\"rectangle\" x1=\"170\" y1=\"408\" x2=\"92\" y2=\"97\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1887\" num=\"36\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"1\" title=\"Avionics Mode\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Avionics Mode\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"459\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The current <B>avionics master mode </B>or sub-mode is displayed in the lower left corner of the HUD. In the NAV master mode, this will indicate the primary navigation source selected (i.e., EGI, VOR, TCN, ILS, LOC, BC, and FIP or FNL when in the SCA navigation mode).</span></P><P><span class='ae'><I><span class='an'><I>The A/A and A/G master mode indications are covered in IFF </I><I>training</I>. <BR></P></span></I></span>]]></text><files mediaID=\"11764\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-138-22secNoFIREwarn2_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"2398\" bldnum=\"1\" type=\"rectangle\" x1=\"600\" y1=\"437\" x2=\"37\" y2=\"20\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1890\" num=\"37\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Maximum G-Load\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Maximum G-Load\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"459\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The </span><span class='ae'>maximum positive G-load </span><span class='an'>incurred is indicated above the current avionics mode. The G-load is displayed in increments of 0.1 G. This is the maximum G placed on the aircraft after takeoff, or since the last time the G display was reset on the MFD by the pilot.</span></P><P><span class='an'></span><span class='an'>You can manually reset the G-load value to &quot;1.0&quot; (on the MFD) as long as an over-G has not occurred in flight.</span> <BR></P>]]></text><files mediaID=\"11765\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-138-22secNoFIREwarn2_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"2399\" bldnum=\"1\" type=\"rectangle\" x1=\"600\" y1=\"426\" x2=\"31\" y2=\"20\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1942\" num=\"38\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Mach Number\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Mach Number\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"456\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<span class='an'>The </span><span class='ae'>current mach number </span><span class='an'>is indicated on the HUD in all master modes. The mach number readout is digitally displayed in increments of 0.01.</span> <BR>]]></text><files mediaID=\"11766\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-138-22secNoFIREwarn2_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"2417\" bldnum=\"1\" type=\"rectangle\" x1=\"614\" y1=\"409\" x2=\"34\" y2=\"20\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1948\" num=\"39\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"1\" title=\"Weapons Delivery Status\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Weapons Status\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"459\" txtHeight=\"166\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<span class='an'>The current<B> </B></span><span class='ae'>weapons delivery status </span><span class='an'>is indicated on the HUD above the mach number. <span class='an'>Either SAFE or ARM is </span></span><span class='an'>displayed depending on the position of the Master Arm Switch.</span> ]]></text><files mediaID=\"8112\" bldnum=\"1\" media=\"T38_SO1109_CBT_ArmCmdlPanel_sm_2off_P46131.jpg\" media_x=\"95\" media_y=\"285\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11767\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-138-22secNoFIREwarn2_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"2418\" bldnum=\"1\" type=\"rectangle\" x1=\"613\" y1=\"399\" x2=\"40\" y2=\"20\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"3700\" bldnum=\"1\" type=\"rectangle\" x1=\"90\" y1=\"277\" x2=\"123\" y2=\"124\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1960\" num=\"40\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Current G-Load\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Current G-Load\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"458\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<span class='an'>The </span><span class='ae'>current G-load </span><span class='an'>is indicated directly above the airspeed scale on the HUD. The G-load is digitally displayed in 0.1 increments. </span>]]></text><files mediaID=\"11768\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-138-22secNoFIREwarn2_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"2419\" bldnum=\"1\" type=\"rectangle\" x1=\"624\" y1=\"243\" x2=\"33\" y2=\"20\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1974\" num=\"41\" revision=\"1.0\" tempType=\"0\" quesType=\"ddT\" randomize=\"1\" title=\"Current AOA\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Current AOA\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"455\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<span class='an'>The </span><span class='ae'>current AOA </span><span class='an'>is displayed above the current G-load and airspeed scale on the upper left side of the HUD. The digital readout is preceded by the alpha (α) symbol. AOA is indicated in 0.01 units from 0.00 to 1.10.</span>]]></text><files mediaID=\"11769\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-138-22secNoFIREwarn2_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"2420\" bldnum=\"1\" type=\"rectangle\" x1=\"608\" y1=\"230\" x2=\"48\" y2=\"20\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1978\" num=\"42\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"1\" title=\"Course/Heading and Range\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Course and Range\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"458\" txtHeight=\"639\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<P><span class='ae'>Course / heading and range </span><span class='an'>data is only displayed in the </span><span class='ae'>NAV master mode</span><span class='an'>. This data is the same as the &quot;boxed&quot; values displayed on the MFD for the course (CRS) or heading (HDG) and the MFD for range (T, A, V, I, E).</span></P><P><span class='an'></span><span class='an'>The top line indicates the selected </span><span class='ae'><span class='an'><i>course</i></span> </span><span class='an'>(or <span class='an'><i>heading</i> </span>with flight director in HDG mode). The bottom line displays the </span><span class='an'><i>range</i> to the primary navigation source</span><span class='an'>. The range prefixes are based on the range source: TCN for TACAN (with or without AAT selected), VOR for the paired DME, EGI for EGI/SCA, and ILS for both paired DME or TACAN range. </span></P>]]></text><files mediaID=\"11770\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-138-22secNoFIREwarn2_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"2421\" bldnum=\"1\" type=\"rectangle\" x1=\"589\" y1=\"174\" x2=\"68\" y2=\"39\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1988\" num=\"43\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Fuel Data Block\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Fuel Data Block\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"457\" txtHeight=\"315\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The </span><span class='ae'>fuel data block display </span><span class='an'>shows the total fuel quantity on the top line and the amount of fuel remaining above Bingo on the second line.</span></P><P><span class='an'></span><span class='an'>In this example, total fuel is 1,900 lbs which should correspond with the fuel amount on the EED.</span></P><P><span class='an'></span><span class='an'>The second line shows 1,000 lbs remaining before you reach Bingo. You can change the Bingo fuel setting on the <span class='ae'>UFCP </span><span class='ae'>SET display</span><span class='an'>.</span></span></P><P><span class='an'><span class='an'></span>If the current quantity is below Bingo fuel, the &quot;fuel remaining above Bingo&quot;&nbsp;line is blank.&nbsp;</span> </P>]]></text><files mediaID=\"11771\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-138-22secNoFIREwarn2_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11772\" bldnum=\"1\" media=\"T38_SO1109_CBT-UFCPsmSETpage_b6.png\" media_x=\"90\" media_y=\"385\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"2423\" bldnum=\"1\" type=\"rectangle\" x1=\"844\" y1=\"226\" x2=\"56\" y2=\"39\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"2424\" bldnum=\"1\" type=\"rectangle\" x1=\"165\" y1=\"563\" x2=\"138\" y2=\"52\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1995\" num=\"44\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"RALT Readout\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"RALT Readout\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"456\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The <span class='ae'>Radar Altimeter </span>(<span class='ae'>RALT</span>) is a radio altimeter that uses a transmitted/received RF signal to determine the current altitude above the ground (AGL).&nbsp;The&nbsp;AGL altitude&nbsp;</span><span class='an'>is&nbsp;indicated by one to four digits inside&nbsp;a box below the altitude scale. This altitude is preceded by an &quot;</span><span class='an'>R</span><span class='an'>&quot;<B> </B>and is indicated&nbsp;in increments of&nbsp;10 feet. RALT accuracy is <span class='an'>±</span> 2 feet or 2%, whichever is greater.</span></P><P><span class='an'></span><span class='an'>The RALT display is removed under any of the following conditions:</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;No signal received from the RALT<BR></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(malfunction or switched OFF)</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Aircraft is outside of RALT limits</span> <span class='an'>(greater than<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;5,000 feet&nbsp;AGL or 45</span><span class='an'>°</span> <span class='an'>bank)</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Aircraft is on the ground (weight-on-wheels)</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;POD (WSSP)&nbsp;selected on </span><span class='an'>MFD&nbsp;WPN page<BR><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (<i><b>Note</b></i> <span class='an'>—</span> Places RALT in standby mode. The <BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; WSSP blocks&nbsp;the RALT transmit/receive <BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; antennas, rendering the system unreliable.)&nbsp; </span></span></P>]]></text><files mediaID=\"11773\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-138-22secNoFIREwarn2_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"2425\" bldnum=\"1\" type=\"rectangle\" x1=\"830\" y1=\"396\" x2=\"68\" y2=\"25\" htext=\"\" colorType=\"1\" /></frame><frame id=\"1998\" num=\"45\" revision=\"1.0\" tempType=\"1\" quesType=\"mcT\" randomize=\"0\" title=\"Minimum Radar Altitude / Alert\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Altitude Alert\" template=\"NotUsed\" numbuilds=\"2\" remediation=\"0\" classlevel=\"0\" txtWidth=\"444\" txtHeight=\"612\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>When </span><span class='ae'>BOTH </span><span class='an'>or </span><span class='ae'>RALT </span><span class='an'>is selected on the UFCP as the source for the altitude alert, the set<span class='an'>&nbsp;RALT minimum altitude </span></span><span class='an'>is displayed below the current RALT readout. This altitude is indicated by up to four digits and is preceded by the letters &quot;</span><span class='ae'>AL</span><span class='an'>.&quot;</span></P><P><span class='an'></span><span class='ad'><I>Click the Forward button to see the &quot;ALTITUDE&quot; alert and UFCP ALT display.</I></span> </P>]]></text><text bldnum=\"2\" ><![CDATA[<span class='an'>When the conditions for the </span><span class='ae'>altitude alert </span><span class='an'>are met, this display </span><span class='ae'>blinks </span><span class='an'>until the ACK button on the UFCP is pressed.</span> ]]></text><files mediaID=\"11774\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-138-22secNoFIREwarn2_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11676\" bldnum=\"2\" media=\"T38_SO1109_CBT_HUD_ALTWarning_b9.mp4\" media_x=\"465\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11775\" bldnum=\"2\" media=\"T38_SO1109_CBT-UFCPsmAltRalt_b6.png\" media_x=\"91\" media_y=\"362\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"2427\" bldnum=\"1\" type=\"rectangle\" x1=\"830\" y1=\"394\" x2=\"67\" y2=\"41\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"2429\" bldnum=\"2\" type=\"rectangle\" x1=\"219\" y1=\"388\" x2=\"83\" y2=\"39\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"2430\" bldnum=\"2\" type=\"rectangle\" x1=\"167\" y1=\"544\" x2=\"133\" y2=\"41\" htext=\"\" colorType=\"1\" /></frame><frame id=\"3380\" num=\"46\" revision=\"1.0\" tempType=\"1\" quesType=\"mcT\" randomize=\"0\" title=\"EGI Data Block\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"EGI Data Block\" template=\"NotUsed\" numbuilds=\"2\" remediation=\"0\" classlevel=\"0\" txtWidth=\"458\" txtHeight=\"635\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The </span><span class='ae'>EGI data block </span><span class='an'>consists of two lines containing EGI navigation information.</span></P><P><span class='an'></span><span class='an'>The </span><span class='an'>top line </span><span class='an'>shows the time-to-go (</span><span class='an'>MM:SS</span><span class='an'>) to reach the EGI steer point at the current groundspeed. If the EGI is in the ATT alignment mode, &quot;</span><span class='an'>ATT</span><span class='an'>&quot; is displayed in place of the TTG readout.</span></P><P><span class='an'></span><span class='an'>The </span><span class='an'><span class='an'>second line shows the current EGI steer point number or ICAO point (e.g., &quot;435 W&quot;)&nbsp;and the distance (e.g., &quot;1.8&quot; NM) to that </span></span><span class='an'>point.</span> </P>]]></text><text bldnum=\"2\" ><![CDATA[<span class='an'>This entry provides the </span><span class='ae'>range <span class='an'>(e.g., &quot;<span class='an'>4.2</span>&quot; NM) </span></span><span class='an'>to the Air-to-Air TACAN of the other participating aircraft. When the TACAN is in the Air-to-Ground mode this display is removed. </span><BR><BR>]]></text><files mediaID=\"11776\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-138-22secNoFIREwarn2_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11801\" bldnum=\"2\" media=\"T38_SO1109_CBT_HUD-VORCDINoDME22secAATAlt Alerts1_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"5227\" bldnum=\"1\" type=\"rectangle\" x1=\"822\" y1=\"452\" x2=\"75\" y2=\"38\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"5229\" bldnum=\"2\" type=\"rectangle\" x1=\"831\" y1=\"434\" x2=\"58\" y2=\"23\" htext=\"\" colorType=\"1\" /></frame><frame id=\"3381\" num=\"47\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Message Window #1\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Message Window #1\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"452\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<span class='an'>The </span><span class='ae'>message window </span><span class='an'>is used to display </span><span class='ae'><span class='an'>Warnings, Cautions, and Advisories</span></span><span class='an'>. The message window moves to the upper part of the HUD below the boresight cross when the gear lever is down.</span> ]]></text><files mediaID=\"11777\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-138-22secNoFIREwarn2_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"5228\" bldnum=\"1\" type=\"rectangle\" x1=\"674\" y1=\"345\" x2=\"150\" y2=\"50\" htext=\"\" colorType=\"1\" /></frame><frame id=\"3382\" num=\"48\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Message Window #2\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Message Window #2\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"451\" txtHeight=\"635\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='ag'>This message window is used to display Warnings, Cautions, and Advisories. The message window moves to the upper part of the HUD below the boresight cross when the gear lever is down.</span></P><P><span class='ag'></span><span class='ae'><B>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Warnings</B></span> <span class='an'><B></B></span><span class=an'>–</span> <span class='an'>Display is boxed and blinks</span> </P>]]></text><files mediaID=\"11677\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD_FIRE_b9.mp4\" media_x=\"470\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /></frame><frame id=\"3383\" num=\"49\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Message Window #3\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Message Window #3\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"441\" txtHeight=\"638\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='ag'>This message window is used to display Warnings, Cautions, and Advisories. The message window moves to the upper part of the HUD below the boresight cross when the gear lever is down.</span></P><P><span class='ag'></span><span class='ag'><B>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Warnings </B></span><span class='ag'>–</span> <span class='ag'>Display is boxed and blinks</span></P><P><span class='ag'></span><span class='ae'><B>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Cautions</B></span> <span class='an'></span><span class=an'>–</span> <span class='an'>Display blinks but not boxed</span> </P>]]></text><files mediaID=\"11681\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD_AVIONICS_b9.mp4\" media_x=\"470\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /></frame><frame id=\"3384\" num=\"50\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Message Window #4\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Message Window #4\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"438\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='ag'>This<B> </B>message window is used to display Warnings, Cautions, and Advisories. The message window moves to the upper part of the HUD below the boresight cross when the gear lever is down.</span></P><P><span class='ag'></span><span class='ag'><B>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Warnings </B></span><span class='ag'>–</span> <span class='ag'>Display is boxed and blinks</span></P><P><span class='ag'></span><span class='ag'><B>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Cautions </B></span><span class='ag'>–</span> <span class='ag'>Display blinks but not boxed</span></P><P><span class='ag'></span><span class='ae'><B>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Advisory </B></span><span class=an'>–</span> <span class='an'>Steady display</span></P><P><span class='an'><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Advisories are only displayed on the<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;HUD, not the MFD.&nbsp;</P></span></span>]]></text><files mediaID=\"11883\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD_FULL_b9.mp4\" media_x=\"470\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /></frame><frame id=\"3385\" num=\"51\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Message Window #5\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Message Window #5\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"438\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='ag'>This message window is used to display Warnings, Cautions, and Advisories. The message window moves to the upper part of the HUD below the boresight cross when the gear lever is down.</span></P><P><span class='ag'></span><span class='ag'><B>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Warnings </B></span><span class='ag'>–</span> <span class='ag'>Display is boxed and blinks</span></P><P><span class='ag'></span><span class='ag'><B>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Cautions </B></span><span class='ag'>–</span> <span class='ag'>Display blinks but not boxed</span></P><P><span class='ag'></span><span class='ag'><B>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Advisory </B></span><span class='ag'>–</span> <span class='ag'>Steady display</span></P><P><span class='ag'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Ad<span class='ag'>visories are only displayed on the<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;HUD, not the MFD. </P><P></span></span><span class='ag'></span><span class='an'><i>Only one alert is displayed at a time</i>. If more than one alert is activated, the </span><span class='an'>higher priority alert </span><span class='an'>is displayed in the window.</span></P><P><span class='an'></span><span class='an'>The visual alert is automatically terminated when the condition no longer exists, or it is removed by pressing the ACK key on the UFCP. With multiple alerts, the next alert in priority is displayed when the previous one is removed.</span> <BR></P>]]></text><files mediaID=\"11778\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-138-22secNoFIREwarn2_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /></frame><frame id=\"2226\" num=\"52\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Time Indications\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Time Indications\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"459\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>Time indications </span><span class='an'>are shown as follows:</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;The </span><span class='ae'>time-of-day </span><span class='an'>is in the<span class='an'>&nbsp;top right corner</span></span><span class='an'>.</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;The </span><span class='ae'>chronometer time </span><span class='an'>is in the </span><span class='ae'><span class='an'>bottom right<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;corner </span></span><span class='an'>(displayed just <span class='an'>outside of <span class='an'><span class='an'>the IFOV <BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;on the HUD</span></span></span></span><span class='an'>).</span></P><P><span class='an'></span><span class='an'>From a normal sitting position in the front seat, you will probably have to move your head slightly to see the time displays. However, these time indications are recorded by the camera as <span class='an'>part <span class='an'>of the HUD display </span></span>and you can view them during the debrief as a reference.</span> </P>]]></text><files mediaID=\"11779\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI-138-22secNoFIREwarn2_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"2448\" bldnum=\"1\" type=\"rectangle\" x1=\"832\" y1=\"492\" x2=\"53\" y2=\"20\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"2449\" bldnum=\"1\" type=\"rectangle\" x1=\"871\" y1=\"130\" x2=\"58\" y2=\"23\" htext=\"\" colorType=\"1\" /></frame><frame id=\"2241\" num=\"53\" revision=\"1.0\" tempType=\"0\" quesType=\"hs\" randomize=\"0\" title=\"Review Question #1\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Review #1\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"990\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>Roll over the following titles to see the associated HUD display.</span></P><P><span class='an'></span><span class='an'></span>&nbsp;</P><P><span class='an'></span>&nbsp;</P><P><span class='an'>Aircraft Heading</span></P><P><span class='an'>Airspeed</span></P><P><span class='an'>RALT readout</span></P><P><span class='an'>Primary Navigation</span></P><P><span class='an'>Selected EGI point</span></P><P><span class='an'>Barometric altitude</span></P><P><span class='an'>Range to Selected VOR</span></P><P><span class='an'>Selected VOR Course</span></P><P><span class='an'>Bingo fuel delta</span></P>]]></text><files mediaID=\"11780\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDICrs270_b9.png\" media_x=\"477\" media_y=\"105\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><hotspot hotSpotID=\"640\" bldNum=\"1\" actionType=\"Display a File\" activateOn=\"Rollover\" action=\"T38_SO1109_CBT_HUD-VORCDICrs270_b9_AircraftHeading_hs640.png\" displayType=\"Invisible\" left=\"5\" top=\"109\" width=\"155\" height=\"35\" mediaX=\"477\" mediaY=\"105\" textWidth=\"100\" textHeight=\"100\" dropShadow=\"0\" /><hotspot hotSpotID=\"641\" bldNum=\"1\" actionType=\"Display a File\" activateOn=\"Rollover\" action=\"T38_SO1109_CBT_HUD-VORCDICrs270_b9_Airspeed_hs641.png\" displayType=\"Invisible\" left=\"4\" top=\"148\" width=\"87\" height=\"27\" mediaX=\"477\" mediaY=\"105\" textWidth=\"100\" textHeight=\"100\" dropShadow=\"0\" /><hotspot hotSpotID=\"642\" bldNum=\"1\" actionType=\"Display a File\" activateOn=\"Rollover\" action=\"T38_SO1109_CBT_HUD-VORCDICrs270_b9_RALTreading_hs642.png\" displayType=\"Invisible\" left=\"3\" top=\"181\" width=\"136\" height=\"30\" mediaX=\"477\" mediaY=\"105\" textWidth=\"100\" textHeight=\"100\" dropShadow=\"0\" /><hotspot hotSpotID=\"643\" bldNum=\"1\" actionType=\"Display a File\" activateOn=\"Rollover\" action=\"T38_SO1109_CBT_HUD-VORCDICrs270_b9_PrimaryNavigation_hs643.png\" displayType=\"Invisible\" left=\"1\" top=\"221\" width=\"181\" height=\"27\" mediaX=\"477\" mediaY=\"105\" textWidth=\"100\" textHeight=\"100\" dropShadow=\"0\" /><hotspot hotSpotID=\"644\" bldNum=\"1\" actionType=\"Display a File\" activateOn=\"Rollover\" action=\"T38_SO1109_CBT_HUD-VORCDICrs270_b9_SelectedEGIpoint_hs644.png\" displayType=\"Invisible\" left=\"2\" top=\"232\" width=\"180\" height=\"30\" mediaX=\"477\" mediaY=\"105\" textWidth=\"100\" textHeight=\"100\" dropShadow=\"0\" /><hotspot hotSpotID=\"645\" bldNum=\"1\" actionType=\"Display a File\" activateOn=\"Rollover\" action=\"T38_SO1109_CBT_HUD-VORCDICrs270_b9_Barometricaltitude_hs645.png\" displayType=\"Invisible\" left=\"2\" top=\"275\" width=\"180\" height=\"25\" mediaX=\"477\" mediaY=\"105\" textWidth=\"100\" textHeight=\"100\" dropShadow=\"0\" /><hotspot hotSpotID=\"646\" bldNum=\"1\" actionType=\"Display a File\" activateOn=\"Rollover\" action=\"T38_SO1109_CBT_HUD-VORCDICrs270_b9_RangetoSelectedVOR_hs646.png\" displayType=\"Invisible\" left=\"4\" top=\"308\" width=\"220\" height=\"25\" mediaX=\"477\" mediaY=\"105\" textWidth=\"100\" textHeight=\"100\" dropShadow=\"0\" /><hotspot hotSpotID=\"647\" bldNum=\"1\" actionType=\"Display a File\" activateOn=\"Rollover\" action=\"T38_SO1109_CBT_HUD-VORCDICrs270_b9_SelectedVORCourse_hs647.png\" displayType=\"Invisible\" left=\"2\" top=\"347\" width=\"211\" height=\"26\" mediaX=\"477\" mediaY=\"105\" textWidth=\"100\" textHeight=\"100\" dropShadow=\"0\" /><hotspot hotSpotID=\"648\" bldNum=\"1\" actionType=\"Display a File\" activateOn=\"Rollover\" action=\"T38_SO1109_CBT_HUD-VORCDICrs270_b9_Bingofueldelta_hs648.png\" displayType=\"Invisible\" left=\"2\" top=\"381\" width=\"152\" height=\"26\" mediaX=\"477\" mediaY=\"105\" textWidth=\"100\" textHeight=\"100\" dropShadow=\"0\" /></frame><frame id=\"2274\" num=\"54\" revision=\"1.0\" tempType=\"0\" quesType=\"ddT\" randomize=\"1\" title=\"Approach Display\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Approach Display\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"456\" txtHeight=\"639\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>When the landing gear lever is lowered during an </span><span class='an'>approach</span><span class='an'>, the </span><span class='ae'><span class='an'><I>heading scale moves to the top of the display</I></span></span><span class='an'>, some <I>additional flight symbology </I>is provided on the HUD, and the <I>TD box is removed </I>(unless the selected destination is between 500 and 599). </span></P><P><span class='an'>The specific symbology displayed depends on the type of approach and navigation source selected. The </span><span class='an'>additional approach symbology </span><span class='an'>includes:</span></P><P><span class='an'></span>&nbsp;</P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2.5</span><span class='an'>°</span> <span class='an'>Descent Reference Line</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;AOA Symbol (Staple)</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Localizer Raw Data Bar</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Glideslope Raw Data Bar</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Pitch/Glideslope FD Steering Symbol</span></P><P><span class='an'></span><span class='an'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Instantaneous Vertical Velocity</span> <span class='an'>Indicator</span></P><P><span class='an'></span>&nbsp;</P><P><span class='an'></span><span class='an'>Each of these approach display items is covered in the following slides.</span> <BR></P>]]></text><files mediaID=\"11781\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-ILS-CDIVDI-AOA fast-IVVbar1_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /></frame><frame id=\"2287\" num=\"55\" revision=\"1.0\" tempType=\"0\" quesType=\"ddT\" randomize=\"1\" title=\"2.5°  Descent Reference Line\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Descent Ref Line\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"454\" txtHeight=\"635\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<span class='an'>A dashed </span><span class='ae'>2.5</span><span class='ae'>°</span> <span class='ae'>descent reference </span><span class='an'>or </span><span class='ae'>dive line </span><span class='an'>is added to the pitch ladder as a reference to aid in making more precise pitch attitude adjustments during the approach.</span>]]></text><files mediaID=\"11782\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-ILS-CDIVDI-AOA fast-IVVbar1_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"2453\" bldnum=\"1\" type=\"rectangle\" x1=\"685\" y1=\"383\" x2=\"128\" y2=\"23\" htext=\"\" colorType=\"1\" /></frame><frame id=\"2307\" num=\"56\" revision=\"1.0\" tempType=\"1\" quesType=\"mcT\" randomize=\"1\" title=\"AOA Symbol\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"AOA Symbol\" template=\"NotUsed\" numbuilds=\"2\" remediation=\"0\" classlevel=\"0\" txtWidth=\"453\" txtHeight=\"382\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The </span><span class='ae'>AOA symbol </span><span class='an'>or<B> </B>&quot;</span><span class='ae'>staple</span><span class='an'>&quot;<B> </B>moves vertically in </span><span class='an'>relation to the FPM </span><span class='an'>to indicate <span class='an'>AOA</span>. When the FPM is level with the top of the symbol, the AOA is 0.55 units. When it is level with the bottom of the symbol, the AOA is 0.65 units. Optimal AOA for the approach is 0.55 to 0.65 units at the center of the symbol. If the AOA is outside of the 0.55 to 0.65 range, the symbol will move above and below the FPM up to half the staple length. </span></P>]]></text><text bldnum=\"2\" ><![CDATA[<P><span class='an'>The relationship between FPM position and AOA remains linear throughout the vertical limits of the indicator (approximately 0.45 to 0.75):</span></P>]]></text><files mediaID=\"11783\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-ILS-CDIVDI-AOA fast-IVVbar1_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11687\" bldnum=\"2\" media=\"T38_SO1109_CBT_HUD_FPMPos_b9_942_626.swf\" media_x=\"53\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"5880\" bldnum=\"1\" type=\"rectangle\" x1=\"714\" y1=\"317\" x2=\"25\" y2=\"65\" htext=\"\" colorType=\"1\" /></frame><frame id=\"2416\" num=\"57\" revision=\"1.0\" tempType=\"1\" quesType=\"ddT\" randomize=\"1\" title=\"Localizer Raw Data Bar\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Localizer Bar\" template=\"NotUsed\" numbuilds=\"5\" remediation=\"0\" classlevel=\"0\" txtWidth=\"459\" txtHeight=\"636\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"10\" colTop=\"407\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The </span><span class='ae'>localizer raw data bar </span><span class='an'>is displayed when<B> </B></span><span class='ae'>ILS</span><span class='an'>, </span><span class='ae'>LOC</span><span class='an'>, or </span><span class='ae'>BC </span><span class='an'>is selected for navigation and a valid localizer signal is received. The localizer bar represents the selected course and shows lateral deviation from it. The bar is stabilized to the aircraft and remains perpendicular to the wings. The bar moves&nbsp;horizontally relative to&nbsp;the FPM and stays vertically centered on it.</span></P><P><span class='an'></span><span class='an'>Lateral movement is limited to the width of the glideslope bar. The tick marks on the glideslope bar correspond to inner/outer dots on the CDI scale and are used as a reference for course deviation.</span> </P>]]></text><text bldnum=\"2\" ><![CDATA[<P><span class='an'>If the signal is not received, both the localizer bar and glideslope bar are </span><span class='an'>dashed and centered </span><span class='an'>on the HUD display. </span></P>]]></text><text bldnum=\"3\" ><![CDATA[<P><span class='aalt'><U>Bar Scale</U>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<U>Deviation</U></span> </P><P><span class='ae'><B>Center&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</B></span><span class='an'>None </span><span class='an'>—</span> <span class='an'>On course</span> </P>]]></text><text bldnum=\"4\" ><![CDATA[<P><span class='ae'><B>Inner ticks&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </B></span><span class='an'>Half beam left / right </span></P>]]></text><text bldnum=\"5\" ><![CDATA[<span class='ae'><B>Outer ticks&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</B></span><span class='an'>Full beam left / right</span> ]]></text><files mediaID=\"11784\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-ILS-CDIVDI-AOA fast-IVVbar1_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11684\" bldnum=\"2\" media=\"T38_SO1109_CBT_HUD_SteeringBarsCTR_b9.mp4\" media_x=\"465\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11700\" bldnum=\"3\" media=\"T38_SO1109_CBT_HUD_SteeringCTR_b9.mp4\" media_x=\"465\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11701\" bldnum=\"4\" media=\"T38_SO1109_CBT_HUD_SteerRngHalf_b9.mp4\" media_x=\"465\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11702\" bldnum=\"5\" media=\"T38_SO1109_CBT_HUD_SteerRngAll_b9.mp4\" media_x=\"465\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"3703\" bldnum=\"1\" type=\"rectangle\" x1=\"760\" y1=\"284\" x2=\"25\" y2=\"85\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"4214\" bldnum=\"1\" type=\"rectangle\" x1=\"707\" y1=\"285\" x2=\"85\" y2=\"25\" htext=\"\" colorType=\"1\" /></frame><frame id=\"2525\" num=\"58\" revision=\"1.0\" tempType=\"1\" quesType=\"ddT\" randomize=\"1\" title=\"Glideslope Raw Data Bar\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Glideslope Bar\" template=\"NotUsed\" numbuilds=\"5\" remediation=\"0\" classlevel=\"0\" txtWidth=\"454\" txtHeight=\"636\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The </span><span class='ae'>glideslope raw data bar </span><span class='an'>is displayed when </span><span class='ae'>ILS </span><span class='an'>is selected. This bar serves as the glideslope indicator and shows deviation from the glidepath.</span> <BR><span class='an'></span>&nbsp;<BR><span class='an'>The glideslope bar is also roll stabilized and remains perpendicular to the localizer bar. The bar is horizontally centered on the FPM and moves left and right with the FPM. Vertical movement is limited to the length of the localizer bar. The tick marks on the localizer bar correspond to upper and lower dots on the </span><span class='ae'>EADI glideslope scale </span><span class='an'>and are used as a reference for glidepath deviation.</span> </P>]]></text><text bldnum=\"2\" ><![CDATA[<P><span class='an'>Until a valid glideslope signal is received, the bar is </span><span class='ae'>dashed and centered </span><span class='an'>on the HUD.</span> </P>]]></text><text bldnum=\"3\" ><![CDATA[<P><span class='aalt'><U>Bar Scale</U>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<U>Deviation</U></span></P><P><span class='aalt'></span><span class='ae'><B>Center&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</B></span><span class='an'>None </span><span class='an'>—</span> <span class='an'>On glideslope</span> </P>]]></text><text bldnum=\"4\" ><![CDATA[<P><span class='ae'><B>Inner ticks&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </B></span><span class='an'>Half beam above / below</span> </P>]]></text><text bldnum=\"5\" ><![CDATA[<span class='ae'><B>Outer ticks&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</B></span><span class='an'>Full beam above / below</span> <BR>]]></text><files mediaID=\"11785\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-ILS-CDIVDI-AOA fast-IVVbar1_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11703\" bldnum=\"2\" media=\"T38_SO1109_CBT_HUD_GSCtrNoSig_b9.mp4\" media_x=\"465\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"1\" loop=\"0\" /><files mediaID=\"11704\" bldnum=\"3\" media=\"T38_SO1109_CBT_HUD_GSCtr_b9.mp4\" media_x=\"465\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"1\" loop=\"0\" /><files mediaID=\"11705\" bldnum=\"4\" media=\"T38_SO1109_CBT_HUD_GSRangeHalf_b9.mp4\" media_x=\"465\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"1\" loop=\"1\" /><files mediaID=\"11706\" bldnum=\"5\" media=\"T38_SO1109_CBT_HUD_GSRangeAll_b9.mp4\" media_x=\"470\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"1\" loop=\"1\" /><highlights highlightID=\"3705\" bldnum=\"1\" type=\"rectangle\" x1=\"707\" y1=\"286\" x2=\"85\" y2=\"25\" htext=\"\" colorType=\"1\" /></frame><frame id=\"2542\" num=\"59\" revision=\"1.0\" tempType=\"1\" quesType=\"mcT\" randomize=\"0\" title=\"Flight Director (FD) Pitch/Bank Steering\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"FD Steering\" template=\"NotUsed\" numbuilds=\"5\" remediation=\"0\" classlevel=\"0\" txtWidth=\"443\" txtHeight=\"634\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The <span class='ae'>flight director</span> (<span class='ae'>FD</span>) provides pitch and bank steering when the FD mode is <span class='ae'>CRS</span>. The FD symbol is displayed when a valid ILS signal is received. If only the localizer signal is received, the FD reverts to the bank steering only mode (circle) described earlier. When the glideslope is received, a vertical tick appears on top of the circle symbol.</span></P>]]></text><text bldnum=\"2\" ><![CDATA[<P><span class='an'>To provide course guidance, the FD steering symbol moves&nbsp;</span><span class='ae'><B>left and&nbsp;right</B></span> <span class='an'><B></B>relative to the FPM and course, indicating the amount of </span><span class='ae'><b>bank</b> </span><span class='an'>required to intercept and maintain the course.</P></span>]]></text><text bldnum=\"3\" ><![CDATA[<P><span class='an'>In addition to providing course guidance, the FD steering symbol moves </span><span class='ae'><B>up and down</B></span> <span class='an'><B></B>relative to the FPM and glidepath, indicating the amount of </span><span class='ae'><b>pitch change </b></span><span class='an'>required to intercept and maintain the glideslope. The maximum pitch commanded is 5 degrees.</P></span>]]></text><text bldnum=\"4\" ><![CDATA[<P><span class='an'>When either the ILS course or glideslope deviation <span class='ae'><b>exceeds full bar deflection</b></span>, an &quot;X&quot; is displayed on the tick as an indicator.</span></P>]]></text><text bldnum=\"5\" ><![CDATA[<P><span class='an'>You should adjust the pitch and bank to keep the FD <span class='ae'>centered under the FPM</span>. The FD symbol and raw data bars are all centered under the FPM when established &quot;</span><span class='an'><I>on course and on glidepath</I></span><span class='an'>.&quot;</span> <BR><span class='an'></span>&nbsp;<BR></P>]]></text><files mediaID=\"11786\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-ILS-CDIVDI-AOA fast-IVVbar1_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11707\" bldnum=\"2\" media=\"T38_SO1109_CBT_HUD_FDRangeHorz_b9.mp4\" media_x=\"465\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"1\" loop=\"1\" /><files mediaID=\"11708\" bldnum=\"3\" media=\"T38_SO1109_CBT_HUD_FDRangeVert_b9.mp4\" media_x=\"465\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"1\" loop=\"1\" /><files mediaID=\"11709\" bldnum=\"4\" media=\"T38_SO1109_CBT_HUD_FDRangeX_b9.mp4\" media_x=\"465\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"1\" loop=\"1\" /><files mediaID=\"11787\" bldnum=\"5\" media=\"T38_SO1109_CBT_HUD-ILS-CDIVDI-AOAperfect_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"5921\" bldnum=\"1\" type=\"arrow\" x1=\"742\" y1=\"279\" x2=\"754\" y2=\"293\" htext=\"\" colorType=\"1\" /></frame><frame id=\"2552\" num=\"60\" revision=\"1.0\" tempType=\"1\" quesType=\"mcT\" randomize=\"1\" title=\"Instantaneous Vertical Velocity (IVV) Indicator\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"IVV\" template=\"NotUsed\" numbuilds=\"3\" remediation=\"0\" classlevel=\"0\" txtWidth=\"426\" txtHeight=\"624\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The </span><span class='ae'>Instantaneous Vertical Velocity Indicator <span class='an'>(</span><span class='an'>IVV</span><span class='an'>)</span> </span><span class='an'>is displayed next to the altitude scale and consists of the following two components:</span> </P>]]></text><text bldnum=\"2\" ><![CDATA[<P><span class='an'>The </span><span class='ae'>vertical velocity scale </span><span class='an'>is displayed next to the altitude scale&nbsp;and indicates climb and descent rates up to 2,000 feet per minute (fpm). The center line represents level flight. Each tick mark on the scale is equal to 500 fpm. Small ticks are used for the 500&nbsp;fpm increments and larger ticks for the 1,000&nbsp;fpm increments.&nbsp;</span></P>]]></text><text bldnum=\"3\" ><![CDATA[&nbsp;<br>]]></text><files mediaID=\"11788\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-ILS-CDIVDI-AOA fast-IVVbar1_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11789\" bldnum=\"2\" media=\"T38_SO1109_CBT_HUD-ILS-CDIVDI-AOA fast-IVVbar1_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><files mediaID=\"11710\" bldnum=\"3\" media=\"T38_SO1109_CBT_HUD_VVIScale_b9_982_669.swf\" media_x=\"20\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><highlights highlightID=\"3707\" bldnum=\"1\" type=\"rectangle\" x1=\"814\" y1=\"252\" x2=\"35\" y2=\"160\" htext=\"\" colorType=\"1\" /><highlights highlightID=\"3711\" bldnum=\"2\" type=\"rectangle\" x1=\"821\" y1=\"251\" x2=\"20\" y2=\"160\" htext=\"\" colorType=\"1\" /></frame><frame id=\"2880\" num=\"61\" revision=\"1.0\" tempType=\"2\" quesType=\"mcT\" randomize=\"1\" title=\"Practice Question #1\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Practice #1\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"426\" txtHeight=\"624\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"0\" colTop=\"333\" colWidth=\"203\" ><text bldnum=\"1\" ><![CDATA[<span class='an'>Based on the HUD indications to the right, which </span><span class='an'>of the following statements best describes the </span><span class='an'>aircraft's current flight attitude or parameters?</span>]]></text><files mediaID=\"11790\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-ILSFD-onVDI-LtofCRS_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><negFeedback><![CDATA[Incorrect.<br>The correct answer is highlighted in Green. <br>The first tick mark on the bank scale represents <br>10 degrees of bank.  Also, the IVV arrow <br>is indicating 1,000 fpm.]]></negFeedback><posFeedback><![CDATA[Correct!]]></posFeedback><distractors randomize=\"1\"><answers id=\"19501\" correct=\"0\" remediation=\"\"><![CDATA[<span class='ax'>Aircraft is banked 10</span><span class='ax'>°</span> <span class='ax'>left, pitch angle is </span><BR><span class='ax'>approximately 3</span><span class='ax'>°</span> <span class='ax'>nose low, and the descent</span><BR><span class='ax'>rate is 1,000 fpm.</span> ]]></answers><answers id=\"19512\" correct=\"1\" remediation=\"\"><![CDATA[<span class='ax'>Aircraft is banked 10</span><span class='ax'>°</span> <span class='ax'>right, pitch angle is </span><BR><span class='ax'>approximately&nbsp;3</span><span class='ax'>°</span> <span class='ax'>nose low, and the descent</span><BR><span class='ax'>rate is 1,000 fpm.</span> ]]></answers><answers id=\"19523\" correct=\"0\" remediation=\"\"><![CDATA[<span class='ax'>Aircraft is banked 5</span><span class='ax'>°</span> <span class='ax'>left, pitch angle is </span><BR><span class='ax'>6</span><span class='ax'>°</span> <span class='ax'>nose low, and the descent </span><span class='ax'>rate is 500 fpm.</span> ]]></answers><answers id=\"19534\" correct=\"0\" remediation=\"\"><![CDATA[<span class='ax'>Aircraft is banked 5</span><span class='ax'>°</span> <span class='ax'>right, pitch angle is </span><BR><span class='ax'>6</span><span class='ax'>°</span> <span class='ax'>nose low, and the descent </span><span class='ax'>rate is 500 fpm.</span> ]]></answers></distractors></frame><frame id=\"2886\" num=\"62\" revision=\"1.0\" tempType=\"2\" quesType=\"mcT\" randomize=\"1\" title=\"Practice Question #2\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Practice #2\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"426\" txtHeight=\"624\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<span class='an'>Based on the HUD indications to the right, which</span> <span class='an'>of the following statements best describes the</span> <span class='an'>aircraft's current position in relation to the ILS?</span> ]]></text><files mediaID=\"11791\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-ILSFD-onVDI-LtofCRS_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><negFeedback><![CDATA[Incorrect.<br>The correct answer is highlighted in Green. <br>The localizer raw data bar is to the right of the FPM <br>which indicates that the aircraft is left of course.]]></negFeedback><posFeedback><![CDATA[Correct!]]></posFeedback><distractors randomize=\"1\"><answers id=\"19541\" correct=\"0\" remediation=\"\"><![CDATA[<span class='ax'>The aircraft is on course </span><BR><span class='ax'>and on the glideslope.</span> ]]></answers><answers id=\"19552\" correct=\"0\" remediation=\"\"><![CDATA[<span class='ax'>The aircraft is on course, </span><BR><span class='ax'>but below the glideslope.</span> ]]></answers><answers id=\"19563\" correct=\"0\" remediation=\"\"><![CDATA[<span class='ax'>The aircraft is right of course, </span><BR><span class='ax'>but on the glideslope.</span> ]]></answers><answers id=\"19574\" correct=\"1\" remediation=\"\"><![CDATA[<span class='ax'>The aircraft is left of course, </span><BR><span class='ax'>but on the glideslope.</span> ]]></answers></distractors></frame><frame id=\"2888\" num=\"63\" revision=\"1.0\" tempType=\"2\" quesType=\"mcT\" randomize=\"1\" title=\"Practice Question #3\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Practice #3\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"426\" txtHeight=\"624\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<span class='an'>Based on the HUD indications to the right, which </span><span class='an'>of the following statements best describes the </span><span class='an'>aircraft's current AOA and airspeed situation?</span>]]></text><files mediaID=\"11792\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VORCDI3pt0-28sec_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><negFeedback><![CDATA[Incorrect.<br>The correct answer is highlighted in Green. <br>The AOA staple will be centered on the FPM<br>when on-speed and 0.6 will be displayed above <br>the airspeed scale.  Also, 170 knots is above <br>the computed final approach airspeed of 165.]]></negFeedback><posFeedback><![CDATA[Correct!]]></posFeedback><distractors randomize=\"1\"><answers id=\"19581\" correct=\"0\" remediation=\"\"><![CDATA[<span class='ax'>The AOA and airspeed indications </span><BR><span class='ax'>are showing the aircraft on-speed.</span> ]]></answers><answers id=\"19592\" correct=\"0\" remediation=\"\"><![CDATA[<span class='ax'>The AOA and airspeed indications </span><BR><span class='ax'>are showing the aircraft slightly slow.</span> ]]></answers><answers id=\"19603\" correct=\"1\" remediation=\"\"><![CDATA[<span class='ax'>The AOA and airspeed indications </span><BR><span class='ax'>are showing the aircraft slightly fast.</span> ]]></answers><answers id=\"19614\" correct=\"0\" remediation=\"\"><![CDATA[<span class='ax'>The AOA is indicating slightly slow,</span> <BR><span class='ax'>but the airspeed is fast.</span> ]]></answers></distractors></frame><frame id=\"3041\" num=\"64\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"1\" title=\"End of Topic\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"End of Topic\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"990\" txtHeight=\"225\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<span class='an'>This concludes the </span><span class='ae'>Navigation Mode Display </span><span class='an'>topic. The last topic will cover the </span><span class='ae'>TCAS Display</span><span class='an'>. </span><BR>]]></text><files mediaID=\"11553\" bldnum=\"1\" media=\"T38_SO1109_CBTsplashjetHUDflag_b9.png\" media_x=\"30\" media_y=\"125\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /></frame></topic><topic id=\"188\" num=\"5\" title=\"TCAS Display\"  type=\"0\"><frame id=\"t188\" num=\"1\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" title=\"Heads-Up Display (HUD)\" attachment=\"\" headphoneIcon=\"None\" menutitle=\"Topic Intro\" template=\"topicIntro\" numbuilds=\"1\" remediation=\"\" classlevel=\"0\" txtWidth=\"1000\" txtHeight=\"800\" txtLeft=\"150\" txtTop=\"0\" columnA=\"\" columnB=\"\" ><text bldnum=\"1\"><![CDATA[<span class='tn'><span class='th'>&emsp;HUD Symbology</span><br>&emsp;&emsp;- Lesson Objectives<br>&emsp;&emsp;- Introduction<br>&emsp;&emsp;- Display and Controls<br>&emsp;&emsp;- NAV Mode Display<br><span class='th'>&emsp;&emsp;- TCAS Display</span><br>]]></text></frame><frame id=\"2897\" num=\"2\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"1\" title=\"Resolution Advisory (RA) Command\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"RA Command\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"443\" txtHeight=\"634\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<span class='an'>The only TCAS information displayed on the HUD is during a </span><span class='ae'>resolution advisory </span><span class='an'>(</span><span class='an'>RA</span><span class='an'>). When the TCAS&nbsp;generates an&nbsp;RA, a </span><span class='ae'>vertical maneuvering command </span><span class='an'>similar to the RA symbol provided on the MFD EADI is displayed on the HUD. The symbol </span><span class='an'>blinks </span><span class='an'>for 3 seconds when if first appears, and then remains on steady for the duration of the RA. An </span><span class='ae'>RA audio command </span><span class='an'>is also activated along with this display.</span> <BR>]]></text><files mediaID=\"11711\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD_TCAS_b9.mp4\" media_x=\"465\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /></frame><frame id=\"4460\" num=\"3\" revision=\"1.0\" tempType=\"0\" quesType=\"ddT\" randomize=\"1\" title=\"RA Display\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"RA Display\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"458\" txtHeight=\"640\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<P><span class='an'>The RA symbol display is stabilized to the horizon and may be displayed above, below, or over the FPM on the HUD. If the actual position of the RA zone happens to be outside of the HUD FOV, the RA symbol is extended and displayed just within the FOV.</span></P><P><span class='an'></span><span class='ad'><B>Two</B></span> <span class='an'>RA command symbols may also be displayed if multiple RA traffic conflicts occur <span class='an'>simultaneously</span>.</span></P><P><span class='an'></span><span class='ad'><I>Click on &quot;Two&quot; above to see an example of two RA symbols displayed.</I></span></P><P><span class='ad'></span><span class='an'>The RA symbol defines the vertical area or </span><span class='ae'>&quot;danger&quot; zone </span><span class='an'>that the pilot should avoid by keeping the FPM outside of it. Your IP can provide more specific guidance on the recommended pilot actions in the event of an RA, both in VFR and IFR conditions.</span> <BR><span class='an'></span>&nbsp;<BR><span class='an'></span>&nbsp;<BR><span class='an'></span>&nbsp;<BR></P>]]></text><files mediaID=\"11794\" bldnum=\"1\" media=\"T38_SO1109_CBT_HUD-VOR13pt8RA AdvisoryFPMbelow_b9.png\" media_x=\"480\" media_y=\"50\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /><hotspot hotSpotID=\"610\" bldNum=\"1\" actionType=\"Display a File\" activateOn=\"Click\" action=\"T38_SO1109_CBT_HUD-VOR13pt8RA AdvisoryFPMbetweenzones_b9_hs610.png\" displayType=\"Invisible\" left=\"1\" top=\"140\" width=\"51\" height=\"34\" mediaX=\"480\" mediaY=\"50\" textWidth=\"100\" textHeight=\"100\" dropShadow=\"0\" /></frame><frame id=\"3042\" num=\"4\" revision=\"1.0\" tempType=\"0\" quesType=\"mcT\" randomize=\"0\" title=\"Conclusion\" attachment=\"\" remAttachment=\"\" headphoneIcon=\"None\" menutitle=\"Conclusion\" template=\"NotUsed\" numbuilds=\"1\" remediation=\"0\" classlevel=\"0\" txtWidth=\"990\" txtHeight=\"80\" txtLeft=\"0\" txtTop=\"0\" columnA=\"\" columnB=\"\" colLeft=\"100\" colTop=\"100\" colWidth=\"250\" ><text bldnum=\"1\" ><![CDATA[<span class='an'>The information presented here will prepare you for your simulator and aircraft missions in the T-38C. </span><span class='ad'><I>Please complete the HUD exercise found in Attachment&nbsp;1 to the SG on the Desktop Trainer at this time.</I></span> <BR>]]></text><files mediaID=\"11554\" bldnum=\"1\" media=\"T38_SO1109_CBTsplashjetHUDflag_b9.png\" media_x=\"30\" media_y=\"125\" controller=\"0\" movenext=\"0\" autoplay=\"0\" loop=\"0\" /></frame></topic></segment></course_data></courseware>";
    break;
